import React from "react";
import { Handshake, ChevronRight } from "lucide-react";
import { THEME } from "../theme";

const Hero = () => {
  return (
    <section
      className="relative overflow-hidden"
      style={{
        background: `linear-gradient(135deg, #A6F0C6 0%, #C3EFFF 40%, #E8FBF1 100%)`, // mint-blue gradient
        color: THEME.TEXT_PRIMARY,
      }}
    >
      {/* Decorative Circles */}
      <div className="absolute -top-20 -right-20 w-80 h-80 bg-[#99F0DA]/40 rounded-full blur-3xl"></div>
      <div className="absolute bottom-0 left-0 w-72 h-72 bg-[#CFFAE0]/60 rounded-full blur-3xl"></div>

      <div className="max-w-7xl mx-auto px-6 py-24 md:py-32 flex flex-col md:flex-row items-center justify-between relative z-10">
        {/* Left Side */}
        <div className="max-w-xl">
          <span
            className="inline-block mb-4 px-4 py-1 text-sm font-semibold rounded-full"
            style={{
              backgroundColor: "rgba(255, 255, 255, 0.4)",
              color: "#14532D",
              backdropFilter: "blur(10px)",
            }}
          >
            üåç Empowering Industries Globally
          </span>

          <h1
            className="text-5xl md:text-6xl font-extrabold mb-6 leading-tight"
            style={{
              color: "#083344",
              textShadow: "0 2px 8px rgba(0,0,0,0.1)",
            }}
          >
            <span className="block">Building the Future</span>
            <span
              style={{
                color: "#089981",
              }}
            >
              With Skilled Manpower
            </span>
          </h1>

          <p
            className="text-lg mb-8"
            style={{
              color: "#374151",
              maxWidth: "36rem",
              lineHeight: "1.7",
            }}
          >
            ARM Group delivers trained, reliable, and professional manpower to
            global industries ‚Äî fostering innovation, growth, and success.
          </p>

          <div className="flex flex-col sm:flex-row gap-4">
            <a
              href="#services"
              className="inline-flex items-center justify-center px-8 py-3 font-semibold rounded-xl shadow-lg hover:scale-105 transition-transform"
              style={{
                background: "linear-gradient(90deg, #10B981, #34D399)",
                color: "white",
              }}
            >
              <Handshake className="w-5 h-5 mr-2" />
              Hire Your Team
            </a>
            <a
              href="#about"
              className="inline-flex items-center justify-center px-8 py-3 border-2 rounded-xl font-medium hover:bg-white/60 backdrop-blur-sm transition"
              style={{
                borderColor: "#10B981",
                color: "#065F46",
              }}
            >
              Learn More
              <ChevronRight className="w-4 h-4 ml-2" />
            </a>
          </div>
        </div>

        {/* Right Side */}
        <div className="mt-12 md:mt-0 relative">
          <div
            className="absolute inset-0 bg-gradient-to-tr from-[#10B981]/40 to-transparent rounded-3xl blur-xl"
            style={{ zIndex: 0 }}
          ></div>
          <img
            src="https://placehold.co/600x400/f0fdf4/064e3b?text=Expert+Team+at+Work"
            alt="Professional Team"
            className="relative rounded-3xl shadow-2xl border-4 border-white"
            style={{ zIndex: 1 }}
          />
        </div>
      </div>
    </section>
  );
};

export default Hero;
