import React, { useState, useEffect, useCallback, useRef } from 'react';
import { 
    Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin, MapPin, Mail, Phone, Facebook, Twitter, 
    Briefcase, Fuel, UtensilsCrossed, Package, Factory, LayoutGrid, Handshake, ChevronRight, MessageSquare, Clock
} from 'lucide-react';

// ===================================
// 0. Color Definitions & Utilities
// ===================================

const DEEP_BROWN = '#544036'; // Primary/Text Color - Replaced with ARM Blue for modern look
const ACCENT_SAND = '#D4A74B'; // Secondary/Accent Color - ARM Gold
const SOFT_CREAM = '#FBF9F6'; // Background Color - Soft Cream

// Theme Colors (Derived from the Admin Dashboard and common corporate branding)
const ARM_BLUE = '#1F3B66'; 
const ARM_GOLD = '#D4A74B';

// Characters used for the scrambling effect
const SCRAMBLE_CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=';
const DURATION = 1000; // Total duration of the scramble effect in ms

/**
 * ScrambleText Component (Animation Utility)
 * Animates text by cycling through random characters before resolving to the final text.
 */
const ScrambleText = ({ text, className, color = ARM_BLUE }) => {
    const [scrambledText, setScrambledText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    const scramble = useCallback(() => {
        if (!text) return;

        const interval = Math.max(10, DURATION / text.length / 2); 
        let iteration = 0;

        const scrambler = setInterval(() => {
            const newText = text.split('').map((char, index) => {
                // Determine how many characters should be resolved
                const progress = iteration / text.length;
                
                if (index < iteration) {
                    return char;
                } else if (index < iteration + Math.floor(text.length * progress * 0.5)) {
                    // Randomly scramble characters
                    return SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];
                } else {
                    return ''; // Keep trailing part empty initially
                }
            }).join('');

            setScrambledText(newText);
            iteration += 1;

            if (iteration >= text.length + 10) { // Add buffer to show a final scramble flash
                clearInterval(scrambler);
                setScrambledText(text);
                setIsComplete(true);
            }
        }, interval);

        return () => clearInterval(scrambler);
    }, [text]);

    useEffect(() => {
        setIsComplete(false);
        setScrambledText('');
        const cleanup = scramble();
        return cleanup;
    }, [text, scramble]);

    return (
        <span 
            className={`${className} transition-opacity duration-500`} 
            style={{ color: isComplete ? color : ARM_GOLD }}
        >
            {scrambledText}
        </span>
    );
};

// ===================================
// 1. Navbar Component
// ===================================

const NavLink = ({ to, children, setIsOpen }) => {
    const handleScroll = (e) => {
        e.preventDefault();
        const targetElement = document.getElementById(to);
        if (targetElement) {
            targetElement.scrollIntoView({ behavior: 'smooth' });
        }
        setIsOpen && setIsOpen(false); // Close menu on mobile click
    };

    return (
        <a 
            href={`#${to}`} 
            onClick={handleScroll}
            className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block font-medium"
        >
            {children}
        </a>
    );
};

const Navbar = ({ toggleView, currentView }) => {
    const [isOpen, setIsOpen] = useState(false);

    const isPublicView = currentView === 'public';

    return (
        <header className="sticky top-0 z-50 bg-white shadow-lg">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                {/* Logo - UPDATED to open in new tab */}
                <div className="text-2xl font-bold text-arm-blue">
                    <a 
                        href="/" 
                        target="_blank" 
                        rel="noopener noreferrer" 
                        className="hover:text-arm-gold transition duration-300"
                    >
                        ARM <span className="text-arm-gold">Group</span>
                    </a>
                </div>

                {/* Desktop Navigation & Toggle Button */}
                <nav className="hidden lg:flex items-center space-x-8">
                    {isPublicView && (
                        <>
                            <NavLink to="hero">Home</NavLink>
                            <NavLink to="about">About Us</NavLink>
                            <NavLink to="services">Services</NavLink>
                            <NavLink to="directors">Leadership</NavLink>
                            <NavLink to="contact">Contact</NavLink> {/* New Contact Link */}
                            <NavLink to="footer">Information</NavLink>
                        </>
                    )}
                    <button 
                        onClick={() => { toggleView(isPublicView ? 'admin' : 'public'); setIsOpen(false); }}
