// 6. Contact Form Component (NEW)
// ===================================

const Contact = () => {
    const [formData, setFormData] = useState({ name: '', email: '', message: '' });
    const [status, setStatus] = useState(''); // 'success', 'error', 'submitting', ''

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setStatus('submitting');
        
        // --- Mock API Call Simulation ---
        setTimeout(() => {
            // In a real app, you would send formData to a backend API here.
            // Mocking a successful submission after 1.5 seconds.
            const isSuccess = Math.random() > 0.1; // 90% chance of success
            
            if (isSuccess) {
                setStatus('success');
                setFormData({ name: '', email: '', message: '' });
            } else {
                setStatus('error');
            }
        }, 1500);
    };

    return (
        <section 
            id="contact" 
            className="bg-gray-50 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 md:py-24"
        >
            <div className="grid lg:grid-cols-2 lg:gap-16 items-start">
                
                {/* Left Column: Text and Information */}
                <div>
                    <h2 className="text-3xl sm:text-4xl font-extrabold text-arm-blue tracking-tight mb-4">
                        Request a Consultation
                    </h2>
                    <p className="text-lg text-gray-700 mb-8">
                        Ready to discuss your manpower requirements? Fill out the form or use the contact details provided to get in touch with our specialist team.
                    </p>
                    
                    <div className="space-y-4">
                        <div className="flex items-center text-gray-700">
                            <Mail className="w-5 h-5 mr-3 text-arm-gold" />
                            <a href="mailto:info@armgroup.com" className="hover:text-arm-blue transition">info@armgroup.com</a>
                        </div>
                        <div className="flex items-center text-gray-700">
                            <Phone className="w-5 h-5 mr-3 text-arm-gold" />
                            <a href="tel:+96611234567" className="hover:text-arm-blue transition">+966 11 234 567</a>
                        </div>
                        <div className="flex items-center text-gray-700">
                            <Clock className="w-5 h-5 mr-3 text-arm-gold" />
                            <span>Mon - Fri: 9:00 AM - 5:00 PM (GMT+3)</span>
                        </div>
                    </div>
                </div>

                {/* Right Column: Contact Form */}
                <div className="bg-white p-8 rounded-xl shadow-2xl mt-12 lg:mt-0">
                    <form onSubmit={handleSubmit} className="space-y-6">
                        
                        <div>
                            <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-arm-blue focus:border-arm-blue transition duration-200"
                                placeholder="Your full name"
                            />
                        </div>

                        <div>
                            <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">Email Address</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-arm-blue focus:border-arm-blue transition duration-200"
                                placeholder="name@company.com"
                            />
                        </div>

                        <div>
                            <label htmlFor="message" className="block text-sm font-medium text-gray-700 mb-1">Your Requirements / Message</label>
                            <textarea
                                id="message"
                                name="message"
                                rows="4"
                                value={formData.message}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-arm-blue focus:border-arm-blue transition duration-200"
                                placeholder="Tell us about your project and manpower needs..."
                            ></textarea>
                        </div>
                        
                        <button
                            type="submit"
                            disabled={status === 'submitting'}
                            className={`w-full px-4 py-3 text-white font-semibold rounded-lg shadow-md transition duration-300 ${
                                status === 'submitting' 
                                    ? 'bg-gray-400 cursor-not-allowed' 
                                    : 'bg-arm-blue hover:bg-arm-gold hover:text-arm-blue'
                            }`}
                        >
                            {status === 'submitting' ? 'Sending...' : 'Send Request'}
                        </button>

                        {/* Status Message Box */}
                        {status === 'success' && (
                            <div className="p-4 bg-green-100 border border-green-400 text-green-700 rounded-lg text-sm text-center">
                                Thank you! Your request has been sent successfully. We'll be in touch soon.
                            </div>
                        )}
                        {status === 'error' && (
                            <div className="p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg text-sm text-center">
                                Oops! Something went wrong. Please try again or call us directly.
                            </div>
                        )}
                    </form>
                </div>
            </div>
        </section>
    );
};

