                        className="px-4 py-2 bg-arm-gold text-arm-blue text-sm font-semibold rounded-lg shadow-md hover:bg-arm-blue hover:text-white transition duration-300"
                    >
                        Back to Public Site
                    </button>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {/* Stat Card 1 */}
                    <div className="bg-white p-6 rounded-xl shadow-lg border-l-4 border-green-500">
                        <p className="text-sm font-medium text-gray-500">Total Applications</p>
                        <p className="text-3xl font-bold text-gray-900">1,250</p>
                    </div>
                    {/* Stat Card 2 */}
                    <div className="bg-white p-6 rounded-xl shadow-lg border-l-4 border-yellow-500">
                        <p className="text-sm font-medium text-gray-500">Pending Reviews</p>
                        <p className="text-3xl font-bold text-gray-900">45</p>
                    </div>
                    {/* Stat Card 3 */}
                    <div className="bg-white p-6 rounded-xl shadow-lg border-l-4 border-arm-blue">
                        <p className="text-sm font-medium text-gray-500">Active Contracts</p>
                        <p className="text-3xl font-bold text-gray-900">32</p>
                    </div>
                </div>

                <div className="mt-10 bg-white p-8 rounded-xl shadow-2xl border-t-4 border-arm-gold">
                    <h3 className="text-2xl font-semibold text-gray-800 mb-6 flex items-center">
                        <TrendingUp className="w-6 h-6 mr-3 text-arm-gold" />
                        Recent Activity
                    </h3>
                    <ul className="space-y-4">
                        <li className="flex justify-between items-center text-gray-700 p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition duration-200">
                            <span>New Engineer application from India</span>
                            <span className="text-xs text-gray-500">2 hours ago</span>
                        </li>
                        <li className="flex justify-between items-center text-gray-700 p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition duration-200">
                            <span>Contract #A901 renewed with PetroCorp</span>
                            <span className="text-xs text-gray-500">Yesterday</span>
                        </li>
                        <li className="flex justify-between items-center text-gray-700 p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition duration-200">
                            <span>System alert: Database backup successful</span>
                            <span className="text-xs text-gray-500">5 minutes ago</span>
                        </li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

// ===================================
// 10. Page Transition Wrapper
// ===================================

const PageTransitionWrapper = ({ children, isPublic }) => {
    const [opacity, setOpacity] = useState(isPublic ? 1 : 0);
    const [display, setDisplay] = useState('block');

    useEffect(() => {
        if (isPublic) {
            setDisplay('block');
            setTimeout(() => setOpacity(1), 10); // Small delay to trigger transition
        } else {
            setOpacity(0);
            setTimeout(() => setDisplay('none'), 500); // Wait for transition to complete
        }
    }, [isPublic]);

    return (
        <div 
            style={{ opacity, display }} 
            className="transition-opacity duration-500"
        >
            {children}
        </div>
    );
};

// ===================================
// 11. Main App Component
// ===================================

const getInitialView = () => {
    // Check if the hash contains the admin key
    return window.location.hash.includes('admin-key-123') ? 'admin' : 'public';
};

const App = () => {
    const [view, setView] = useState(getInitialView); // 'public' or 'admin' or 'fading-out'

    const toggleView = (targetView) => {
        if (targetView === 'admin' && view === 'public') {
            // Initiate fade out
            setView('fading-out');
            setTimeout(() => {
                // After fade out, change hash and switch to admin view
                window.location.hash = 'admin-key-123';
                setView('admin');
            }, 500); 
        } else {
            // Direct switch (admin -> public or public -> admin via direct hash change)
            if (targetView === 'admin') {
                 window.location.hash = 'admin-key-123';
            } else {
                 window.location.hash = '';
            }
            setView(targetView);
        }
    };

    useEffect(() => {
        const handleHashChange = () => {
            const newView = getInitialView();
            // This is only for external hash changes, internal navigation uses handleNavigation
            if (view !== newView && view !== 'fading-out') {
                setView(newView);
            }
        };
        window.addEventListener('hashchange', handleHashChange);
        return () => window.removeEventListener('hashchange', handleHashChange);
    }, [view]);

    const currentView = view === 'fading-out' ? 'public' : view;

    return (
        <div className="font-sans min-h-screen" style={{ backgroundColor: SOFT_CREAM }}>
            {/* The PageTransitionWrapper handles the full fade when toggling Admin/Public */}
            {(currentView === 'public' || view === 'fading-out') ? (
                <PageTransitionWrapper isPublic={view === 'public'}>
                    <PublicView toggleView={toggleView} view={currentView} />
                </PageTransitionWrapper>
            ) : (
                // The Admin Dashboard remains static
                <AdminDashboard toggleView={toggleView} />
            )}
        </div>
    );
};

export default App;
