import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin, MapPin, Mail, Phone, Facebook, Twitter, Briefcase, ChevronRight, Fuel, UtensilsCrossed, Package, Factory, LayoutGrid, Handshake, Truck, Eye, Target, CheckCircle, Star, Heart, Lightbulb } from 'lucide-react';

// ===================================
// 0. Color Definitions & Utilities
// ===================================

// const DEEP_BROWN = '#544036'; // Primary/Text Color - Not used directly in styles
// const ACCENT_SAND = '#A88F68'; // Secondary/Accent Color - Not used directly in styles
const SOFT_CREAM = '#FBF9F6'; // Background Color
const ARM_BLUE = '#1F3B66'; // Secondary deep blue for contrast
const ARM_GOLD = '#D4A74B'; // Accent gold

// Characters used for the scrambling effect
const SCRAMBLE_CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=';
const DURATION = 1000; // Total duration of the scramble effect in ms

// Utility function to get a random character from the set
const getRandomChar = () => SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];

/**
 * ScrambleText Component (Animation Utility)
 */
const ScrambleText = ({ text, className, color = ARM_BLUE }) => {
    const [scrambledText, setScrambledText] = useState(text || '');
    const intervalRef = useRef(null);

    useEffect(() => {
        if (!text) return;

        let frame = 0;
        const totalFrames = 30; // Number of frames for the animation
        const intervalTime = DURATION / totalFrames;
        
        intervalRef.current = setInterval(() => {
            const newText = text.split('').map((char, index) => {
                const charProgress = (frame * intervalTime) / DURATION;
                const charDelay = index / text.length;
                
                // If the character's designated time has passed, use the final character
                if (charProgress > 0.5 + charDelay * 0.5) {
                    return char;
                }
                
                // Otherwise, scramble it
                return getRandomChar();
            }).join(''); // Crucial: Join the array of characters back into a single string

            setScrambledText(newText);
            frame++;

            if (frame > totalFrames) {
                clearInterval(intervalRef.current);
                setScrambledText(text); // Ensure final text is set exactly
            }
        }, intervalTime);

        // Cleanup function for the interval
        return () => {
            clearInterval(intervalRef.current);
        };
    }, [text]); // Restart effect when text changes

    return (
        <span 
            className={className} 
            style={{ color: color }}
        >
            {scrambledText}
        </span>
    );
};

// ===================================
// 1. Navbar Component
// ===================================

const NavLink = ({ to, children, setIsOpen }) => (
    <a 
        href={`#${to}`} 
        onClick={() => setIsOpen && setIsOpen(false)} // Close menu on mobile click
        className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block"
    >
        {children}
    </a>
);

const Navbar = ({ toggleView, currentView }) => {
    const [isOpen, setIsOpen] = useState(false);

    const isPublicView = currentView === 'public';

    return (
        <header className="sticky top-0 z-50 bg-white shadow-md">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                {/* Logo with ScrambleText */}
                <div className="text-2xl font-bold text-arm-blue">
                    <ScrambleText text="ARM" className="inline-block" color={ARM_BLUE} /> 
                    <span className="text-arm-gold"> Group</span>
                </div>

                {/* Desktop Navigation & Toggle Button */}
                <nav className="hidden lg:flex items-center space-x-8">
                    {isPublicView && (
                        <>
                            <NavLink to="hero">Home</NavLink>
                            {/* NEW: Company Profile Link */}
                            <NavLink to="profile">Profile</NavLink>
                            <NavLink to="about">About Us</NavLink>
                            <NavLink to="services">Services</NavLink>
                            <NavLink to="directors">Leadership</NavLink>
                            <NavLink to="footer">Contact</NavLink>
                        </>
                    )}
                    <button 
                        onClick={() => toggleView(isPublicView ? 'admin' : 'public')}
                        className={`px-4 py-2 text-sm font-semibold rounded-lg shadow-md transition duration-300 
                            ${isPublicView 
                                ? 'bg-arm-blue text-white hover:bg-arm-gold hover:text-arm-blue' 
                                : 'bg-arm-gold text-arm-blue hover:bg-arm-blue hover:text-white'}`
                        }
                    >
                        {isPublicView ? 'Admin Login' : 'Back to Site'}
                    </button>
                </nav>

                {/* Mobile Menu Button */}
                <div className="lg:hidden">
                    <button 
