// ===================================
// 9. Public View & App Components
// ===================================

const PublicView = ({ toggleView, view }) => {
    return (
        <div className="bg-soft-cream min-h-screen">
            <Navbar toggleView={toggleView} currentView={view} />
            <main>
                <Hero />
                {/* NEW: Company Profile component integrated */}
                <CompanyProfile /> 
                <About />
                <Services />
                <Directors />
            </main>
            <Footer />
        </div>
    );
};


const PageTransitionWrapper = ({ children, isPublic }) => {
    const [animate, setAnimate] = useState(false);

    useEffect(() => {
        // Start fade-in animation when component is mounted or visibility changes
        setAnimate(true);
    }, [isPublic]);

    return (
        <div 
            className={`transition-opacity duration-500 ease-in-out ${animate ? 'opacity-100' : 'opacity-0'}`}
        >
            {children}
        </div>
    );
};

const getInitialView = () => {
    // Check hash for admin key
    if (window.location.hash === '#admin-key-123') {
        return 'admin';
    }
    return 'public';
};

export default function App() {
    // State can be 'public', 'admin', or 'fading-out'
    const [view, setView] = useState(getInitialView());

    const toggleView = (targetView) => {
        if (view === targetView) return;

        if (view === 'public') {
            // Initiate fade-out first
            setView('fading-out');
            setTimeout(() => {
                window.location.hash = 'admin-key-123';
                setView('admin');
            }, 500); 
        } else {
            if (targetView === 'admin') {
                 window.location.hash = 'admin-key-123';
            } else {
                 window.location.hash = '';
            }
            setView(targetView);
        }
    };

    useEffect(() => {
        const handleHashChange = () => {
            const newView = getInitialView();
            // This is only for external hash changes, internal navigation uses handleNavigation
            if (view !== newView && view !== 'fading-out') {
                setView(newView);
            }
        };
        window.addEventListener('hashchange', handleHashChange);
        return () => window.removeEventListener('hashchange', handleHashChange);
    }, [view]);

    const currentView = view === 'fading-out' ? 'public' : view;

    return (
        <div className="font-sans">
            {/* Tailwind color definitions */}
            <style jsx global>{`
                :root {
                    --arm-blue: ${ARM_BLUE};
                    --arm-gold: ${ARM_GOLD};
                    --soft-cream: ${SOFT_CREAM};
                }
                .text-arm-blue { color: var(--arm-blue); }
                .bg-arm-blue { background-color: var(--arm-blue); }
                .hover\\:bg-arm-blue\\/10:hover { background-color: rgba(31, 59, 102, 0.1); }
                .border-arm-blue { border-color: var(--arm-blue); }

                .text-arm-gold { color: var(--arm-gold); }
                .bg-arm-gold { background-color: var(--arm-gold); }
                .hover\\:bg-arm-gold:hover { background-color: var(--arm-gold); }
                .bg-arm-gold\\/10 { background-color: rgba(212, 167, 75, 0.1); }
                .border-arm-gold { border-color: var(--arm-gold); }
                .ring-arm-gold\\/50 { --tw-ring-color: rgba(212, 167, 75, 0.5); }

                .bg-soft-cream { background-color: var(--soft-cream); }
            `}</style>
            
            {/* The PageTransitionWrapper handles the full fade when toggling Admin/Public */}
            {(currentView === 'public' || view === 'fading-out') ? (
                <PageTransitionWrapper isPublic={view === 'public'}>
                    <PublicView toggleView={toggleView} view={currentView} />
                </PageTransitionWrapper>
            ) : (
                // The Admin Dashboard remains static
                <AdminDashboard toggleView={toggleView} />
            )}
        </div>
    );
}
