import React from 'react';
// FIX: Changing the path to resolve correctly in the build environment.
// The correct relative path from 'src/components/' to 'src/hooks/useScrollAnimation.js' is '../hooks/useScrollAnimation.js'. 
// We are reverting to this standard relative path, but removing the erroneous './' prefix that was causing the failure.
import useScrollAnimation from '../hooks/useScrollAnimation.js'; 

const Hero = () => {
    const [ref, isVisible] = useScrollAnimation(0.2);

    return (
        <section 
            id="hero" 
            ref={ref}
            className="relative h-[80vh] md:h-[90vh] bg-cover bg-center"
            style={{ 
                backgroundImage: "url('https://placehold.co/1920x1080/0d1a33/fdb813?text=ARM+Group+Manpower+Solutions')", 
                transition: 'opacity 1s ease-out, transform 1s ease-out',
                opacity: isVisible ? 1 : 0,
                transform: isVisible ? 'translateY(0)' : 'translateY(20px)'
            }}
        >
            {/* Overlay for text readability */}
            <div className="absolute inset-0 bg-arm-blue bg-opacity-70"></div>
            
            <div className="relative section-container h-full flex items-center">
                <div className="max-w-3xl text-center md:text-left">
                    <h1 className="text-6xl sm:text-7xl font-black text-white leading-tight mb-4">
                        Global Manpower Solutions
                    </h1>
                    <p className="text-xl sm:text-2xl text-gray-200 mb-8 font-light">
                        Specializing in skilled labor rental, local transfer, and overseas recruitment for diverse industries.
                    </p>
                    <a 
                        href="#services"
                        className="inline-block px-8 py-3 text-lg font-bold rounded-full bg-arm-gold text-arm-blue hover:bg-yellow-500 transition duration-300 shadow-xl transform hover:scale-105"
                    >
                        Explore Our Services
                    </a>
                </div>
            </div>
        </section>
    );
};

export default Hero;
