import React from 'react';
import useScrollAnimation from '../hooks/useScrollAnimation.js';
import { Briefcase, Users, Zap } from 'lucide-react';

const About = () => {
    // Scroll animation hook: path corrected to "../hooks/useScrollAnimation.js"
    const [ref, isVisible] = useScrollAnimation({ threshold: 0.1 });

    const aboutImage = "https://placehold.co/800x600/E8C046/215797?text=Skilled+Workforce";

    const features = [
        { icon: Briefcase, title: "Specialized Expertise", description: "Focused on Oil & Gas, Hospitality, and Construction, ensuring targeted solutions." },
        { icon: Users, title: "Flexible Manpower", description: "Providing staff on rental, local transfer, and overseas recruitment basis." },
        { icon: Zap, title: "Efficiency & Excellence", description: "Our services reduce operational costs while guaranteeing high-quality professional delivery." }
    ];

    return (
        <section 
            id="about" 
            // Tailwind classes providing padding and max-width, matching the removed .section-container CSS
            className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 md:py-24 transition-opacity duration-1000 ease-in-out"
            ref={ref}
            style={{ opacity: isVisible ? 1 : 0 }}
        >
            <div className="lg:grid lg:grid-cols-2 lg:gap-12 items-center">
                {/* Text Content */}
                <div className="mb-10 lg:mb-0">
                    <h2 className="text-sm font-semibold uppercase text-arm-gold mb-2">
                        About ARM Group
                    </h2>
                    {/* Heading styles previously defined by .heading-primary */}
                    <h3 className="text-4xl sm:text-5xl font-extrabold text-arm-blue tracking-tight mb-6">
                        Powering Your Success with Precision Manpower
                    </h3>
                    
                    <p className="text-lg text-gray-600 mb-6">
                        ARM Group specializes in providing **skilled manpower** on a rental and local transfer basis, as well as recruiting from overseas to meet diverse client requirements across multiple industries.
                    </p>
                    <p className="text-lg text-gray-600 mb-8">
                        Our services ensure that clients have access to **qualified professionals** whenever needed, helping them maintain efficiency, reduce operational costs, and achieve long-term success. We focus on matching the right talent to the right role, every time.
                    </p>

                    {/* Feature Cards */}
                    <div className="space-y-6">
                        {features.map((feature, index) => (
                            <div key={index} className="flex items-start">
                                <feature.icon className="w-6 h-6 text-arm-blue mr-3 flex-shrink-0" />
                                <div>
                                    <h4 className="font-bold text-arm-blue">{feature.title}</h4>
                                    <p className="text-gray-500 text-sm">{feature.description}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Image Section */}
                <div className="hidden lg:block">
                    <img 
                        src={aboutImage} 
                        alt="ARM Group Manpower Solutions"
                        className="rounded-xl shadow-2xl w-full h-auto object-cover border-4 border-arm-gold"
                        onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/800x600/E8C046/215797?text=ARM+Manpower+Solutions"}}
                    />
                </div>
            </div>
        </section>
    );
};

export default About;
