import React, { useState } from "react";
import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import About from "../components/About";
import Services from "../components/Services";
import Directors from "../components/AdminDashboard"; // or rename if needed
import Contact from "../components/Footer"; // or separate Contact component
import Footer from "../components/Footer";
import { SOFT_CREAM } from "../theme";

const MainPage = () => {
  const [view, setView] = useState("public");

  return (
    <div >
      <Navbar toggleView={setView} currentView={view} />
      <>
        <Hero />
        <About />
        <Services />
        <Directors />
        <Contact />
        <Footer />
      </>
    </div>
  );
};

export default MainPage;
