// src/App.jsx
import React, { useState, useEffect, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
  useLocation,
} from "react-router-dom";

import {
  Menu,
  X,
  Globe,
  Users,
  TrendingUp,
  HardHat,
  Wrench,
  Zap,
  Shield,
  Linkedin,
  MapPin,
  Mail,
  Phone,
  Facebook,
  Twitter,
  Briefcase,
  ChevronRight,
  Fuel,
  UtensilsCrossed,
  Package,
  Factory,
  LayoutGrid,
  Handshake,
  Truck,
  Eye,
  Target,
  CheckCircle,
  Star,
  Heart,
  Lightbulb,
} from "lucide-react";

/*
  Full App.jsx — consolidated file including:
  - theme colors & utilities
  - ScrambleText logo animation
  - Navbar (updated to open Company Profile in NEW TAB)
  - Hero
  - CompanyProfilePage (standalone route)
  - About, Services, Directors, Footer
  - AdminDashboard (placeholder)
  - PublicView, PageTransition wrapper
  - Router with /, /company-profile, /admin
  - Fade animations on mount
*/

/* =========================
   0. Color definitions
   ========================= */
const SOFT_CREAM = "#FBF9F6";
const ARM_BLUE = "#1F3B66";
const ARM_GOLD = "#D4A74B";

/* =========================
   0.5 Scramble utilities
   ========================= */
const SCRAMBLE_CHARS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=";
const DURATION = 900;
const getRandomChar = () =>
  SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];

/* =========================
   ScrambleText Component
   ========================= */
const ScrambleText = ({ text, className, color = ARM_BLUE }) => {
  const [scrambledText, setScrambledText] = useState(text || "");
  const intervalRef = useRef(null);

  useEffect(() => {
    if (!text) return;
    let frame = 0;
    const totalFrames = 30;
    const intervalTime = DURATION / totalFrames;

    intervalRef.current = setInterval(() => {
      const newText = text
        .split("")
        .map((char, index) => {
          const charProgress = (frame * intervalTime) / DURATION;
          const charDelay = index / Math.max(1, text.length);
          if (charProgress > 0.5 + charDelay * 0.5) return char;
          return getRandomChar();
        })
        .join("");
      setScrambledText(newText);
      frame++;
      if (frame > totalFrames) {
        clearInterval(intervalRef.current);
        setScrambledText(text);
      }
    }, intervalTime);

    return () => clearInterval(intervalRef.current);
  }, [text]);

  return (
    <span className={className} style={{ color }}>
      {scrambledText}
    </span>
  );
};

/* =========================
   Navbar (opens Company Profile in new tab)
   ========================= */
const NavLink = ({ to, children, setIsOpen }) => (
  <a
    href={`#${to}`}
    onClick={() => setIsOpen && setIsOpen(false)}
    className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block"
  >
    {children}
  </a>
);

const Navbar = ({ toggleView, currentView }) => {
  const [isOpen, setIsOpen] = useState(false);
  const isPublicView = currentView === "public";

  const openCompanyProfileInNewTab = () => {
    // open the route in a new tab/window
    window.open("/company-profile", "_blank", "noopener,noreferrer");
  };

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
        {/* Logo */}
        <div
          className="text-2xl font-bold text-arm-blue cursor-pointer"
          onClick={() => toggleView("public")}
        >
          <ScrambleText text="ARM" className="inline-block" color={ARM_BLUE} />{" "}
          <span className="text-arm-gold"> Group</span>
        </div>

        {/* Desktop nav */}
        <nav className="hidden lg:flex items-center space-x-8">
          {isPublicView && (
            <>
              <NavLink to="hero">Home</NavLink>
              <button
                onClick={openCompanyProfileInNewTab}
                className="text-gray-700 hover:text-arm-gold transition duration-200"
              >
                Company Profile
              </button>
              <NavLink to="about">About Us</NavLink>
              <NavLink to="services">Services</NavLink>
              <NavLink to="directors">Leadership</NavLink>
              <NavLink to="footer">Contact</NavLink>
            </>
          )}
          <button
            onClick={() => toggleView(isPublicView ? "admin" : "public")}
            className={`px-4 py-2 text-sm font-semibold rounded-lg shadow-md transition duration-300 ${
              isPublicView
                ? "bg-arm-blue text-white hover:bg-arm-gold hover:text-arm-blue"
                : "bg-arm-gold text-arm-blue hover:bg-arm-blue hover:text-white"
            }`}
          >
            {isPublicView ? "Admin Login" : "Back to Site"}
          </button>
        </nav>

        {/* mobile menu button */}
        <div className="lg:hidden">
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="p-2 rounded-md text-arm-blue hover:bg-gray-100 focus:outline-none"
          >
            {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
      </div>

      {/* mobile menu */}
      {isOpen && (
        <div className="lg:hidden fixed inset-0 backdrop-blur-sm px-4 pt-2 pb-4 space-y-2 bg-white border-t">
          {isPublicView && (
            <>
              <NavLink to="hero" setIsOpen={setIsOpen}>
                Home
              </NavLink>
              <button
                onClick={() => {
                  openCompanyProfileInNewTab();
                  setIsOpen(false);
                }}
                className="w-full text-left px-4 py-2 text-gray-700 hover:text-arm-gold transition duration-200"
              >
                Company Profile
              </button>
              <NavLink to="about" setIsOpen={setIsOpen}>
                About Us
              </NavLink>
              <NavLink to="services" setIsOpen={setIsOpen}>
                Services
              </NavLink>
              <NavLink to="directors" setIsOpen={setIsOpen}>
                Leadership
              </NavLink>
              <NavLink to="footer" setIsOpen={setIsOpen}>
                Contact
              </NavLink>
            </>
          )}

          <button
            onClick={() => {
              toggleView(isPublicView ? "admin" : "public");
              setIsOpen(false);
            }}
            className="w-full text-left px-4 py-2 bg-arm-blue text-white text-sm font-semibold rounded-md shadow-md hover:bg-arm-gold hover:text-arm-blue transition duration-300 mt-2"
          >
            {isPublicView ? "Admin Login" : "Back to Site"}
          </button>
        </div>
      )}
    </header>
  );
};

/* =========================
   Hero Component
   ========================= */
const Hero = () => {
  return (
    <section
      id="hero"
      className="relative bg-white pt-16 pb-24 lg:pt-32 lg:pb-40 overflow-hidden"
    >
      <div className="absolute inset-0 z-0 opacity-8">
        <div
          className="w-full h-full"
          style={{
            backgroundImage: `linear-gradient(45deg, ${SOFT_CREAM} 25%, transparent 25%, transparent 75%, ${SOFT_CREAM} 75%, ${SOFT_CREAM}), linear-gradient(45deg, ${SOFT_CREAM} 25%, ${ARM_BLUE} 25%, ${ARM_BLUE} 75%, ${SOFT_CREAM} 75%, ${SOFT_CREAM})`,
            backgroundSize: "30px 30px",
          }}
        />
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className="lg:grid lg:grid-cols-12 lg:gap-12 items-center">
          <div className="lg:col-span-6 xl:col-span-7">
            <span className="inline-flex items-center text-sm font-semibold text-arm-blue bg-arm-blue/10 rounded-full px-3 py-1 mb-4">
              <Truck className="w-4 h-4 mr-2" />
              Global Manpower Solutions
            </span>

            <h1 className="text-6xl font-extrabold text-arm-blue tracking-tight mb-6 sm:text-7xl">
              Powering Your Projects with{" "}
              <span className="text-arm-gold">Skilled Talent</span>
            </h1>

            <p className="text-xl text-gray-700 max-w-lg mb-10">
              We deliver qualified, trained, and motivated manpower on rental,
              local transfer, and overseas recruitment basis for critical
              sectors.
            </p>

