/* Fade wrapper for simple mount animation */
const FadeInWrapper = ({ children }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => {
    setMounted(true);
  }, []);
  return <div className={`transition-opacity duration-500 ${mounted ? "opacity-100" : "opacity-0"}`}>{children}</div>;
};

export default function App() {
  // We'll keep the simple toggle behavior: admin opens in new tab OR public view remains
  const [view, setView] = useState("public");

  const toggleView = (target) => {
    if (target === "admin") {
      // Open admin dashboard in new tab as you requested admin behaves as separate
      window.open("/admin", "_blank", "noopener,noreferrer");
      return;
    }
    setView("public");
  };

  return (
    <Router>
      <FadeInWrapper>
        <Routes>
          {/* Home (public) */}
          <Route
            path="/"
            element={<PublicView toggleView={toggleView} view={view} />}
          />

          {/* Company Profile opens in a new tab, but also must be a route so the new tab can render it */}
          <Route
            path="/company-profile"
            element={<CompanyProfilePage />}
          />

          {/* Admin route for direct access in new tab if opened */}
          <Route
            path="/admin"
            element={<AdminDashboard toggleView={toggleView} />}
          />

          {/* Fallback: render public view */}
          <Route
            path="*"
            element={<PublicView toggleView={toggleView} view={view} />}
          />
        </Routes>
      </FadeInWrapper>
    </Router>
  );
}
