// src/App.jsx
import React, { useState, useEffect, useRef } from "react";
import {
  Routes,
  Route,
  useNavigate
} from "react-router-dom";
import { THEME } from './theme';
import {
  Menu,
  X,
  Users,
  HardHat,
  Linkedin,
  MapPin,
  Mail,
  Phone,
  Briefcase,
  ChevronRight,
  Fuel,
  UtensilsCrossed,
  Package,
  Factory,
  LayoutGrid,
  Handshake,
  Truck,
  Eye,
  Target,
  CheckCircle,
  Star,
  Heart,
  Lightbulb,
  Globe,
} from "lucide-react";

/* =========================
   Neutral Clean Theme Colors
   ========================= */
// const BG_LIGHT = "#edfdffff"; // off-white
// const THEME.MUTED = "#6B7280"; // gray-500/600
// const THEME.ACCENT = "#0EA5E9"; // blue-gray for subtle THEME.ACCENTs
// const CARD_BG = "#FFFFFF"; // white cards
// const SOFT_BORDER = "#E6E7E9"; // subtle separators

/* =========================
   ScrambleText helper (kept subtle)
   ========================= */
const SCRAMBLE_CHARS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=";
const DURATION = 900;
const getRandomChar = () =>
  SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];

const ScrambleText = ({ text, className, color = THEME.ACCENT }) => {
  const [scrambledText, setScrambledText] = useState(text || "");
  const intervalRef = useRef(null);

  useEffect(() => {
    if (!text) return;
    let frame = 0;
    const totalFrames = 24;
    const intervalTime = Math.max(10, DURATION / totalFrames);

    intervalRef.current = setInterval(() => {
      const newText = text
        .split("")
        .map((ch, idx) => {
          const progress = (frame * intervalTime) / DURATION;
          const delay = idx / Math.max(1, text.length);
          if (progress > 0.5 + delay * 0.5) return ch;
          return getRandomChar();
        })
        .join("");
      setScrambledText(newText);
      frame++;
      if (frame > totalFrames) {
        clearInterval(intervalRef.current);
        setScrambledText(text);
      }
    }, intervalTime);

    return () => clearInterval(intervalRef.current);
  }, [text]);

  return (
    <span className={className} style={{ color, fontFeatureSettings: "'tnum' 1" }}>
      {scrambledText}
    </span>
  );
};

/* =========================
   Navbar (opens Company Profile in new tab)
   Clean, minimal: no heavy shadows
   ========================= */
const NavLink = ({ to, children, setIsOpen }) => (
  <a
    href={`#${to}`}
    onClick={() => setIsOpen && setIsOpen(false)}
    className="text-THEME.MUTED hover:text-THEME.ACCENT transition duration-150 block py-2 lg:py-0 lg:inline-block"
    style={{ color: THEME.MUTED }}
  >
    {children}
  </a>
);

const Navbar = ({ toggleView, currentView }) => {
  const [isOpen, setIsOpen] = useState(false);
  const isPublic = currentView === "public";

  const openCompanyProfile = () => {
    window.open("/company-profile", "_blank", "noopener,noreferrer");
  };

  return (
    <header
      className="sticky top-0 z-40"
      style={{  borderBottom: `1px solid ${THEME.SOFT_BORDER}` }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex items-center justify-between">
        <div
          className="text-lg font-semibold cursor-pointer"
          onClick={() => toggleView("public")}
          style={{ color:THEME.ACCENT }}
        >
          <ScrambleText text="ARM" className="inline-block" color={THEME.ACCENT} />
          <span style={{ color: THEME.MUTED, marginLeft: 6 }}>Group</span>
        </div>

        <nav className="hidden lg:flex items-center space-x-8">
          {isPublic && (
            <>
              <NavLink to="hero">Home</NavLink>

              <button
                onClick={openCompanyProfile}
                className="text-sm transition"
                style={{ color: THEME.MUTED }}
              >
                Company Profile
              </button>

              <NavLink to="about">About</NavLink>
              <NavLink to="services">Services</NavLink>
              <NavLink to="directors">Leadership</NavLink>
              <NavLink to="footer">Contact</NavLink>
            </>
          )}

          <button
            onClick={() => toggleView(isPublic ? "admin" : "public")}
            className="px-3 py-1 rounded-md text-sm border transition"
            style={{
              borderColor: THEME.SOFT_BORDER,
              color: THEME.ACCENT,
              background: "transparent",
            }}
          >
            {isPublic ? "Admin" : "Back"}
          </button>
        </nav>

        <div className="lg:hidden">
          <button onClick={() => setIsOpen(!isOpen)} className="p-2 rounded-md">
            {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
          </button>
        </div>
      </div>

      {isOpen && (
        <div
          className="lg:hidden px-4 pb-4"
          style={{ borderTop: `1px solid ${THEME.SOFT_BORDER}` }}
        >
          <div className="space-y-2">
            <NavLink to="hero" setIsOpen={setIsOpen}>
              Home
            </NavLink>
            <button
              onClick={() => {
                openCompanyProfile();
                setIsOpen(false);
              }}
              className="w-full text-left py-2"
              style={{ color: THEME.MUTED }}
            >
              Company Profile
            </button>
            <NavLink to="about" setIsOpen={setIsOpen}>
              About
            </NavLink>
            <NavLink to="services" setIsOpen={setIsOpen}>
              Services
            </NavLink>
            <NavLink to="directors" setIsOpen={setIsOpen}>
              Leadership
            </NavLink>
            <NavLink to="footer" setIsOpen={setIsOpen}>
              Contact
            </NavLink>

            <button
              onClick={() => {
                toggleView(currentView === "public" ? "admin" : "public");
                setIsOpen(false);
              }}
              className="w-full text-left py-2 mt-2"
              style={{ color: THEME.MUTED }}
            >
              {currentView === "public" ? "Admin" : "Back"}
            </button>
          </div>
        </div>
      )}
    </header>
  );
};

/* =========================
   Hero â€” minimal
   ========================= */
const Hero = () => (
  <section id="hero"  className="py-20">
    <div style={{ color: BG.ACCENT }} className="max-w-5xl mx-auto px-4 text-center">
      <h1 style={{ color: THEME.ACCENT }} className="text-4xl font-extrabold mb-4">
        Powering Projects with <span style={{ color: THEME.MUTED }}>Skilled Talent</span>
      </h1>
      <p style={{ color: THEME.MUTED }} className="max-w-2xl mx-auto mb-8">
        Qualified, trained and motivated manpower for industries â€” delivered with clarity and care.
      </p>

      <div className="flex items-center justify-center gap-4">
        <a
          href="#footer"
          className="px-4 py-2 rounded-md text-sm"
          style={{ border: `1px solid ${THEME.SOFT_BORDER}`, color: THEME.ACCENT }}
        >
          Hire Talent
        </a>
        <a
          href="#services"
          className="px-4 py-2 rounded-md text-sm"
          style={{ border: `1px solid ${THEME.SOFT_BORDER}`, color: THEME.ACCENT }}
        >
          Our Services
        </a>
      </div>
    </div>
  </section>
);

/* =========================
   CompanyProfilePage (standalone route)
   Neutral clean style; includes Back to Home button
   ========================= */
const CompanyProfilePage = () => {
  const navigate = useNavigate();

  return (
    <div style={{ minHeight: "100vh", paddingBottom: 80 }}>
      <div className="max-w-6xl mx-auto px-4 py-10">
        <div className="flex items-center justify-between mb-8">
          <div style={{ color: THEME.ACCENT, fontWeight: 600 }}>
            <ScrambleText text="ARM" color={THEME.ACCENT} /> <span style={{ color: THEME.MUTED, marginLeft: 6 }}>Group</span>
          </div>

          <div className="flex gap-3">
            <button
              onClick={() => navigate("/")}
              className="px-3 py-2 rounded-md text-sm"
              style={{ border: `1px solid ${THEME.SOFT_BORDER}`, color: THEME.ACCENT }}
            >
              â† Back
            </button>
            <a
              href={`${process.env.PUBLIC_URL || ""}/ARM_Group_Presentation[1].pdf`}
              target="_blank"
              rel="noopener noreferrer"
              className="px-3 py-2 rounded-md text-sm"
              style={{ border: `1px solid ${THEME.SOFT_BORDER}`, color: THEME.ACCENT }}
            >
              Open PDF
            </a>
          </div>
        </div>

        <div style={{borderRadius: 12, overflow: "hidden" }}>
          {/* iframe for PDF (public folder) â€” fallback content is textual */}
          <iframe
            title="Company Profile PDF"
            src={`${process.env.PUBLIC_URL || ""}/ARM_Group_Presentation[1].pdf`}
            width="100%"
            height="720"
            style={{ border: "none", display: "block" }}
          />
        </div>

        {/* summary / textual content beneath PDF */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
          <div style={{ borderRadius: 10, padding: 18 }}>
            <h3 style={{ color: THEME.ACCENT, fontWeight: 700, marginBottom: 8 }}>Company Overview</h3>
            <p style={{ color: THEME.MUTED }}>
              ARM Group provides skilled manpower on rental, local transfer and overseas recruitment basis across multiple industries â€” focused on compliance, quality, and timely mobilization.
            </p>
          </div>

          <div style={{ borderRadius: 10, padding: 18 }}>
            <h3 style={{ color: THEME.ACCENT, fontWeight: 700, marginBottom: 8 }}>Core Values</h3>
            <ul style={{ color: THEME.MUTED, marginLeft: 18 }}>
              <li>Integrity</li>
              <li>Excellence</li>
              <li>Commitment</li>
              <li>Teamwork</li>
              <li>Innovation</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

/* =========================
   About (minimal cards)
   ========================= */
const FeatureCard = ({ icon: Icon, title, description }) => (
  <div style={{ borderRadius: 10, padding: 18 }}>
    <div className="flex items-start gap-4">
      <div style={{ width: 44, height: 44, display: "grid", placeItems: "center", borderRadius: 8, border: `1px solid ${THEME._BORDER}` }}>
        <Icon className="w-5 h-5" style={{ color: THEME.MUTED }} />
      </div>
      <div>
        <h4 style={{ color: THEME.ACCENT, fontWeight: 700 }}>{title}</h4>
        <p style={{ color: THEME.MUTED, marginTop: 6 }}>{description}</p>
      </div>
    </div>
  </div>
);

const About = () => (
  <section id="about" className="py-16">
    <div className="max-w-6xl mx-auto px-4">
      <div className="text-center mb-10">
        <h2
          style={{ color: THEME.ACCENT, fontWeight: 700 }}
          className="text-3xl mb-2"
        >
          The ARM Group Advantage
        </h2>
        <p
          style={{
            color: THEME.MUTED,
            maxWidth: 720,
            margin: "0 auto",
          }}
        >
          Skilled manpower solutions tailored for operational excellence and
          compliance.
        </p>
      </div>

     <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <FeatureCard
          icon={Briefcase}
          title="Sector Specialization"
          description="Oil & Gas, Construction, Hospitality, and Logistics."
        />
        <FeatureCard
          icon={Users}
          title="Rapid Mobilization"
          description="Pre-screened talent pool for fast deployment."
        />
        <FeatureCard
          icon={Handshake}
          title="Compliance & Safety"
          description="Adherence to international labor and safety standards."
        />
        <FeatureCard
          icon={Globe}
          title="Global Reach"
          description="Local transfers and overseas recruitment capability."
        />
      </div>
    </div>
  </section>
);

/* =========================
   Services (minimal grid)
   ========================= */
const ServiceCard = ({ icon: Icon, title, description }) => (
  <div style={{  borderRadius: 10, padding: 18 }}>
    <div className="flex items-center gap-4">
      <div style={{ width: 44, height: 44, display: "grid", placeItems: "center", borderRadius: 8 }}>
        <Icon className="w-5 h-5" style={{ color: THEME.MUTED }} />
      </div>
      <div>
        <h4 style={{ color: THEME.ACCENT, fontWeight: 700 }}>{title}</h4>
        <p style={{ color: THEME.MUTED, marginTop: 6 }}>{description}</p>
      </div>
    </div>
  </div>
);

const Services = () => (
  <section id="services" className="py-16" >
    <div className="max-w-6xl mx-auto px-4">
      <div className="text-center mb-10">
        <h2 style={{ color: THEME.ACCENT, fontWeight: 700 }} className="text-3xl mb-2">Our Services</h2>
        <p style={{ color: THEME.MUTED, maxWidth: 720, margin: "0 auto" }}>
          Qualified professionals across key industries.
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {[
          { icon: Fuel, title: "Oil & Gas", description: "Engineers & technicians for rigs and refineries." },
          { icon: HardHat, title: "Construction", description: "Supervisors, skilled labor and managers." },
          { icon: UtensilsCrossed, title: "Hospitality", description: "Trained chefs, waitstaff and housekeepers." },
          { icon: Package, title: "Logistics", description: "Warehouse & supply chain personnel." },
          { icon: Factory, title: "Manufacturing", description: "Operators, mechanics & quality staff." },
          { icon: LayoutGrid, title: "General Manpower", description: "Helpers, cleaners, admin support." },
        ].map((s, i) => (
          <ServiceCard key={i} {...s} />
        ))}
      </div>
    </div>
  </section>
);

/* =========================
   Directors (minimal cards)
   ========================= */
const DirectorCard = ({ name, title, image, description, linkedin }) => (
  <div style={{borderRadius: 10, padding: 18, textAlign: "center" }}>
    <img src={image} alt={name} style={{ width: 96, height: 96, borderRadius: "50%", objectFit: "cover", margin: "0 auto 12px" }} />
    <h4 style={{ color: THEME.ACCENT, fontWeight: 700 }}>{name}</h4>
    <p style={{ color: THEME.MUTED, marginTop: 6, fontSize: 14 }}>{title}</p>
    <p style={{ color: THEME.MUTED, marginTop: 8, fontSize: 13 }}>{description}</p>
    <a href={linkedin} target="_blank" rel="noopener noreferrer" style={{ display: "inline-block", marginTop: 10, color: THEME.ACCENT }}>
      <Linkedin className="w-4 h-4 inline-block" /> Connect
    </a>
  </div>
);

const Directors = () => (
  <section id="directors" className="py-16">
    <div className="max-w-6xl mx-auto px-4">
      <div className="text-center mb-10">
        <h2 style={{ color: THEME.ACCENT, fontWeight: 700 }} className="text-3xl mb-2">Leadership</h2>
        <p style={{ color: THEME.MUTED }}>Meet the team.</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {[
          { name: "Fahad Al-Mubarak", title: "CEO", image: "https://placehold.co/150x150/cccccc/ffffff?text=CEO", description: "Vision & digital transformation", linkedin: "#" },
          { name: "Amal Saleh", title: "COO", image: "https://placehold.co/150x150/cccccc/ffffff?text=COO", description: "Operations & compliance", linkedin: "#" },
          { name: "Omar Kholid", title: "Head of TA", image: "https://placehold.co/150x150/cccccc/ffffff?text=HTA", description: "Talent acquisition lead", linkedin: "#" },
        ].map((d, i) => <DirectorCard key={i} {...d} />)}
      </div>
    </div>
  </section>
);

/* =========================
   Footer (minimal)
   ========================= */
const Footer = () => (
  <footer id="footer" style={{  padding: "36px 0" }}>
    <div className="max-w-7xl mx-auto px-4">
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "flex-start", gap: 24, flexWrap: "wrap" }}>
        <div style={{ minWidth: 220 }}>
          <div style={{ fontWeight: 700, color: THEME.ACCENT }}>ARM <span style={{ color: THEME.MUTED }}>Group</span></div>
          <p style={{ color: THEME.MUTED, marginTop: 8 }}>Skilled manpower solutions focused on quality and compliance.</p>
        </div>

        <div style={{ display: "flex", gap: 24, flexWrap: "wrap" }}>
          <div>
            <div style={{ fontWeight: 700, color: THEME.ACCENT }}>Quick Links</div>
            <ul style={{ color: THEME.MUTED, marginTop: 8 }}>
              <li><a href="#hero" style={{ color: THEME.MUTED }}>Home</a></li>
              <li><a href="#about" style={{ color: THEME.MUTED }}>About</a></li>
              <li><a href="#services" style={{ color: THEME.MUTED }}>Services</a></li>
              <li><a href="#directors" style={{ color: THEME.MUTED }}>Leadership</a></li>
            </ul>
          </div>

          <div>
            <div style={{ fontWeight: 700, color: THEME.ACCENT }}>Contact</div>
            <div style={{ color: THEME.MUTED, marginTop: 8 }}>
              <div><Phone className="inline-block" style={{ verticalAlign: "middle", marginRight: 6 }} /> +966 50 123 4567</div>
              <div style={{ marginTop: 6 }}><Mail className="inline-block" style={{ verticalAlign: "middle", marginRight: 6 }} /> info@armgroup.sa</div>
            </div>
          </div>
        </div>
      </div>

      <div style={{ textAlign: "center", marginTop: 24, color: THEME.MUTED }}>
        &copy; {new Date().getFullYear()} ARM Group. All rights reserved.
      </div>
    </div>
  </footer>
);

/* =========================
   AdminDashboard (opens in new tab OR route)
   Minimal styling
   ========================= */
const AdminDashboard = ({ toggleView }) => {
  return (
    <div style={{ minHeight: "100vh", padding: 36 }}>
      <div className="max-w-6xl mx-auto px-4">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 18 }}>
          <h2 style={{ color: THEME.ACCENT, fontWeight: 700 }}>Admin Dashboard</h2>
          <button
            onClick={() => toggleView("public")}
            style={{ border: `1px solid ${THEME.SOFT_BORDER}`, padding: "8px 12px", borderRadius: 6, color: THEME.ACCENT }}
          >
            Back
          </button>
        </div>

        <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12 }}>
          <div style={{borderRadius: 8, padding: 18 }}>
            <div style={{ color: THEME.MUTED }}>Total Applications</div>
            <div style={{ fontSize: 22, fontWeight: 700, marginTop: 6 }}>1,250</div>
          </div>
          <div style={{  borderRadius: 8, padding: 18 }}>
            <div style={{ color: THEME.MUTED }}>Pending Reviews</div>
            <div style={{ fontSize: 22, fontWeight: 700, marginTop: 6 }}>45</div>
          </div>
          <div style={{borderRadius: 8, padding: 18 }}>
            <div style={{ color: THEME.MUTED }}>Active Contracts</div>
            <div style={{ fontSize: 22, fontWeight: 700, marginTop: 6 }}>32</div>
          </div>
        </div>
      </div>
    </div>
  );
};

/* =========================
   PublicView wrapper
   ========================= */
const PublicView = ({ toggleView, view }) => {
  return (
    <div style={{ minHeight: "100vh" }}>
      <Navbar toggleView={toggleView} currentView={view} />
      <main>
        <Hero />
        <About />
        <Services />
        <Directors />
      </main>
      <Footer />
    </div>
  );
};

/* =========================
   Fade wrapper for small mount animation
   ========================= */
const FadeWrapper = ({ children }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => {
    const t = setTimeout(() => setMounted(true), 20);
    return () => clearTimeout(t);
  }, []);
  return <div style={{ transition: "opacity 300ms ease", opacity: mounted ? 1 : 0 }}>{children}</div>;
};

/* =========================
   Final App (no BrowserRouter included here)
   Assumes index.js mounts <BrowserRouter><App/></BrowserRouter>
   ========================= */
export default function App() {
  const [view, setView] = useState("public");

  const toggleView = (target) => {
    if (target === "admin") {
      // open admin as new tab (keeps main site intact)
      window.open("/admin", "_blank", "noopener,noreferrer");
      return;
    }
    setView("public");
  };

  return (
    <FadeWrapper>
      <Routes>
        <Route
          path="/"
          element={<PublicView toggleView={toggleView} view={view} />}
        />
        <Route path="/company-profile" element={<CompanyProfilePage />} />
        <Route path="/admin" element={<AdminDashboard toggleView={toggleView} />} />
        {/* fallback to public view */}
        <Route path="*" element={<PublicView toggleView={toggleView} view={view} />} />
      </Routes>
    </FadeWrapper>
  );
}
