// NEW ICON IMPORTS: Ensure this line includes all 9 icons
import {  Factory, Hotel, Truck, Building, HeartPulse, ShoppingCart, Mic } from 'lucide-react';
// ===================================
// IMAGE IMPORTS (UPDATED PATHS and all client images)
// ===================================
