// ===================================
// CONSTANTS & COLORS
// ===================================
const DARK_BG = '#1A1A1A'; // Deep Dark Gray/Black
const DEEP_BROWN = '#544036'; // Dark Earthy Brown
const ACCENT_SAND = '#D4B892'; // Muted Light Sand/Gold
const GLASS_ACCENT = '#9E7E50'; // Richer Gold/Brass for buttons/highlights
const SOFT_CREAM = '#FBF9F6'; // Off-White/Cream for backgrounds
const MAX_WORD_LENGTH = 12; // Max word length for ScrambleText

// ===================================
// UTILITY FUNCTIONS
// ===================================

// Placeholder for ScrambleText (not implemented here, but assumes a working component exists)
const ScrambleText = ({ text, className, color }) => {
    // In a real application, this would handle the scrambling animation.
    // For this demonstration, it just renders the text with color.
    return <span className={className} style={{ color }}>{text}</span>;
};

// ===================================
// FIREBASE SETUP & CONTEXT UTILS
// ===================================

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

let db, auth;

// Global Firebase initialization function
const initFirebase = () => {
    if (Object.keys(firebaseConfig).length === 0) {
        console.error("Firebase config is empty. Firestore will not be available.");
        return { db: null, auth: null };
    }
    const app = initializeApp(firebaseConfig);
    const firestore = getFirestore(app);
    const authn = getAuth(app);
    setLogLevel('silent'); // Keep the console clean
    return { db: firestore, auth: authn };
};


// ===================================
// GENERIC COMPONENTS
// ===================================

const FadeInSection = ({ children, delay = 0.2, y = 50 }) => {
    return (
        <motion.div
            initial={{ opacity: 0, y: y }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: delay }}
            viewport={{ once: true, amount: 0.2 }}
        >
            {children}
        </motion.div>
    );
};

// ===================================
// ADMIN DASHBOARD
// ===================================

const AdminDashboard = ({ toggleView }) => {
    const [messages, setMessages] = useState([]);
    const [dbInstance, setDbInstance] = useState(null);
    const [authInstance, setAuthInstance] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userId, setUserId] = useState(null);
    const [newMessageText, setNewMessageText] = useState('');
    const messagesEndRef = useRef(null);

    // 1. Firebase Initialization & Authentication
    useEffect(() => {
        const { db: firestore, auth: authn } = initFirebase();
        if (firestore && authn) {
            setDbInstance(firestore);
            setAuthInstance(authn);

            const handleAuth = async () => {
                try {
                    if (initialAuthToken) {
                        const userCredential = await signInWithCustomToken(authn, initialAuthToken);
                        setUserId(userCredential.user.uid);
                    } else {
                        const userCredential = await signInAnonymously(authn);
                        setUserId(userCredential.user.uid);
                    }
                } catch (error) {
                    console.error("Firebase Authentication failed:", error);
                    // Fallback to a random ID if auth fails completely
                    setUserId(crypto.randomUUID()); 
                } finally {
                    setLoading(false);
                }
            };
            handleAuth();
        } else {
            setLoading(false);
            setUserId(crypto.randomUUID());
        }
    }, []);

    // 2. Real-time Message Listener (using public data for admin to see all chats)
    useEffect(() => {
        if (!dbInstance || loading) return;

        const chatCollectionPath = `/artifacts/${appId}/public/data/adminChat`;
        const q = query(collection(dbInstance, chatCollectionPath), orderBy('timestamp', 'asc'), limit(100));

        const unsubscribe = onSnapshot(q, (snapshot) => {
            const fetchedMessages = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setMessages(fetchedMessages);
        }, (error) => {
            console.error("Error listening to messages: ", error);
        });

        return () => unsubscribe();
    }, [dbInstance, loading]);

    // 3. Auto-scroll to latest message
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // 4. Send Message Handler
    const handleSendMessage = async () => {
        if (!newMessageText.trim() || !dbInstance || !userId) return;

        const chatCollectionPath = `/artifacts/${appId}/public/data/adminChat`;
        const messageData = {
            senderId: 'admin',
            text: newMessageText,
            timestamp: serverTimestamp(),
            // Store a simplified username for display
            userName: 'Admin' 
        };

        try {
            await addDoc(collection(dbInstance, chatCollectionPath), messageData);
            setNewMessageText('');
        } catch (e) {
            console.error("Error adding document: ", e);
        }
    };
    
    if (loading) {
        return <div className="p-8 text-center text-gray-500">Loading Admin Dashboard...</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-4 sm:p-8">
            <header className="flex justify-between items-center mb-8 border-b pb-4">
                <h1 className="text-3xl font-bold text-gray-800">Admin Live Chat Dashboard</h1>
                <button
                    onClick={() => toggleView('public')}
                    className="flex items-center text-white bg-red-600 hover:bg-red-700 px-4 py-2 rounded-full shadow-lg transition duration-200"
                >
                    <X className="w-5 h-5 mr-2" />
                    Exit Admin View
                </button>
            </header>
            
            <p className="text-sm text-gray-500 mb-4">
                You are logged in as: <span className="font-mono text-xs p-1 bg-gray-200 rounded">{userId || 'N/A'}</span>
            </p>

            <div className="flex flex-col h-[70vh] bg-white rounded-xl shadow-2xl overflow-hidden">
                {/* Message Display Area */}
                <div className="flex-grow p-4 overflow-y-auto space-y-4">
                    {messages.length === 0 ? (
                        <p className="text-center text-gray-400 mt-10">No messages yet. Start a conversation!</p>
                    ) : (
                        messages.map((msg) => (
                            <div key={msg.id} className={`flex ${msg.senderId === 'admin' ? 'justify-end' : 'justify-start'}`}>
                                <div className={`max-w-xs md:max-w-md lg:max-w-lg p-3 rounded-xl shadow-md ${
                                    msg.senderId === 'admin'
                                        ? 'bg-blue-600 text-white rounded-br-none'
                                        : 'bg-gray-200 text-gray-800 rounded-tl-none'
                                }`}>
                                    <p className="font-semibold text-xs mb-1 opacity-80">
                                        {msg.userName || (msg.senderId === 'admin' ? 'Admin' : 'Client')}
                                    </p>
                                    <p>{msg.text}</p>
                                    <p className="text-right text-xs mt-1 opacity-60">
                                        {msg.timestamp?.toDate ? msg.timestamp.toDate().toLocaleTimeString() : '...'}
                                    </p>
                                </div>
                            </div>
                        ))
                    )}
                    <div ref={messagesEndRef} />
                </div>

                {/* Input Area */}
                <div className="p-4 border-t bg-gray-50">
                    <div className="flex space-x-2">
                        <input
                            type="text"
                            value={newMessageText}
                            onChange={(e) => setNewMessageText(e.target.value)}
                            onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                            placeholder="Type a response..."
                            className="flex-grow p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                        />
                        <button
                            onClick={handleSendMessage}
                            className="bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-200 shadow-md"
                        >
                            Send
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};


// ===================================
// CHAT WIDGET
// ===================================

const ChatWidget = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [newMessageText, setNewMessageText] = useState('');
    const [dbInstance, setDbInstance] = useState(null);
    const [authInstance, setAuthInstance] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userId, setUserId] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);
    const messagesEndRef = useRef(null);
    const [userName, setUserName] = useState('');

    // 1. Firebase Initialization & Authentication
    useEffect(() => {
        const { db: firestore, auth: authn } = initFirebase();
        if (firestore && authn) {
            setDbInstance(firestore);
            setAuthInstance(authn);

            const unsubscribeAuth = onAuthStateChanged(authn, (user) => {
                if (user) {
                    setUserId(user.uid);
                    setIsAuthReady(true);
                    // Use a simple, shortened ID as a default client name
                    setUserName(`Client-${user.uid.substring(0, 4)}`); 
                }
            });

            // Initial sign-in attempt
            const handleAuth = async () => {
                try {
                    if (initialAuthToken) {
                        await signInWithCustomToken(authn, initialAuthToken);
                    } else {
                        await signInAnonymously(authn);
                    }
                } catch (error) {
                    console.error("Client Firebase Authentication failed:", error);
                } finally {
                    setLoading(false);
                }
            };
            handleAuth();
            
            return () => unsubscribeAuth();
        } else {
            setLoading(false);
            setUserId(crypto.randomUUID());
            setIsAuthReady(true);
        }
    }, []);

    // 2. Real-time Message Listener
    useEffect(() => {
        if (!dbInstance || !isAuthReady) return;

        const chatCollectionPath = `/artifacts/${appId}/public/data/adminChat`;
        const q = query(collection(dbInstance, chatCollectionPath), orderBy('timestamp', 'asc'), limit(100));

        const unsubscribe = onSnapshot(q, (snapshot) => {
            const fetchedMessages = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setMessages(fetchedMessages);
        }, (error) => {
            console.error("Error listening to client messages: ", error);
        });

        return () => unsubscribe();
    }, [dbInstance, isAuthReady]);
    
    // 3. Auto-scroll to latest message
    useEffect(() => {
        if (isOpen) {
            messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages, isOpen]);

    // 4. Send Message Handler
    const handleSendMessage = async () => {
        if (!newMessageText.trim() || !dbInstance || !userId || !isAuthReady) return;

        const chatCollectionPath = `/artifacts/${appId}/public/data/adminChat`;
        const messageData = {
            senderId: userId,
            text: newMessageText,
            timestamp: serverTimestamp(),
            userName: userName 
        };

        try {
            await setDoc(doc(collection(dbInstance, chatCollectionPath)), messageData);
            setNewMessageText('');
        } catch (e) {
            console.error("Error adding client document: ", e);
        }
    };
    
    // Toggle function
    const toggleChat = () => setIsOpen(!isOpen);

    return (
        <>
            {/* Chat Icon Button */}
            <button
                onClick={toggleChat}
                className="fixed bottom-6 right-6 z-50 p-4 rounded-full text-white shadow-xl transition-all duration-300 transform hover:scale-110"
                style={{ backgroundColor: GLASS_ACCENT }}
                aria-label={isOpen ? "Close Chat" : "Open Chat"}
            >
                {isOpen ? <X size={28} /> : <MessageCircle size={28} />}
            </button>

            {/* Chat Window */}
            <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: isOpen ? 1 : 0, scale: isOpen ? 1 : 0.8 }}
                transition={{ duration: 0.3 }}
                className="fixed bottom-24 right-6 w-80 h-[450px] bg-white rounded-xl shadow-2xl z-40 flex flex-col overflow-hidden"
                style={{ display: isOpen ? 'flex' : 'none' }}
            >
                {/* Header */}
                <div className="p-4 text-white font-bold rounded-t-xl" style={{ backgroundColor: DEEP_BROWN }}>
                    Live Chat Support
                </div>
                
                {/* Message Display Area */}
                <div className="flex-grow p-4 overflow-y-auto space-y-3">
                    {loading || !isAuthReady ? (
                        <p className="text-center text-gray-400 mt-10">Connecting...</p>
                    ) : (
                        messages.length === 0 ? (
                            <p className="text-center text-gray-400 mt-10 text-sm">Welcome! Ask us anything about our services.</p>
                        ) : (
                            messages.map((msg) => (
                                <div key={msg.id} className={`flex ${msg.senderId === userId ? 'justify-end' : 'justify-start'}`}>
                                    <div className={`max-w-[75%] p-2 rounded-lg shadow-sm ${
                                        msg.senderId === userId
                                            ? 'bg-blue-500 text-white rounded-br-none'
                                            : 'bg-gray-100 text-gray-800 rounded-tl-none'
                                    }`}>
                                        <p className="text-xs font-semibold mb-1 opacity-80">
                                            {msg.senderId === userId ? 'You' : msg.userName || 'Admin'}
                                        </p>
                                        <p className="text-sm">{msg.text}</p>
                                        <p className="text-right text-[10px] mt-1 opacity-60">
                                            {msg.timestamp?.toDate ? msg.timestamp.toDate().toLocaleTimeString() : '...'}
                                        </p>
                                    </div>
                                </div>
                            ))
                        )
                    )}
                    <div ref={messagesEndRef} />
                </div>

                {/* Input Area */}
                <div className="p-3 border-t bg-gray-50">
                    <div className="flex space-x-2">
                        <input
                            type="text"
                            value={newMessageText}
                            onChange={(e) => setNewMessageText(e.target.value)}
                            onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                            placeholder="Your message..."
                            className="flex-grow p-2 border border-gray-300 rounded-lg text-sm focus:ring-blue-500 focus:border-blue-500"
                            disabled={!isAuthReady}
                        />
                        <button
                            onClick={handleSendMessage}
                            className="bg-blue-600 text-white px-3 py-2 rounded-lg text-sm hover:bg-blue-700 transition duration-200"
                            disabled={!isAuthReady || !newMessageText.trim()}
                        >
                            Send
                        </button>
                    </div>
                </div>
            </motion.div>
        </>
    );
};

