const Navbar = ({ toggleView, currentView }) => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const navItems = [
        { name: 'Services', href: '#services' },
        { name: 'Sectors', href: '#sectors' },
        { name: 'Clients', href: '#clients' },
        { name: 'About', href: '#about' },
        { name: 'Contact', href: '#contact' },
    ];

    return (
        <nav className="sticky top-0 z-40 shadow-lg" style={{ backgroundColor: DARK_BG }}>
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex justify-between items-center h-20">
                    {/* Logo/Brand */}
                    <div className="flex-shrink-0">
                        <a href="#hero" className="text-2xl font-black tracking-wider" style={{ color: ACCENT_SAND }}>
                            ARM GROUP
                        </a>
                    </div>

                    {/* Desktop Navigation */}
                    <div className="hidden md:flex md:items-center space-x-6">
                        {navItems.map((item) => (
                            <a
                                key={item.name}
                                href={item.href}
                                className="text-sm font-medium transition duration-200 hover:text-white"
                                style={{ color: ACCENT_SAND }}
                            >
                                {item.name}
                            </a>
                        ))}
                        {/* Conditional Admin/Profile Button */}
                        <motion.button
                            onClick={() => currentView === 'public' ? toggleView('company-profile') : toggleView('public')}
                            className="ml-4 px-4 py-2 text-sm font-semibold rounded-full transition duration-300 shadow-md"
                            style={{ backgroundColor: GLASS_ACCENT, color: DARK_BG }}
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            {currentView === 'public' ? 'Company Profile' : 'Main Page'}
                        </motion.button>
                        <motion.button
                            onClick={() => toggleView('admin')}
                            className="ml-2 px-4 py-2 text-sm font-semibold rounded-full transition duration-300 shadow-md border"
                            style={{ borderColor: GLASS_ACCENT, color: ACCENT_SAND }}
                            whileHover={{ scale: 1.05, backgroundColor: ACCENT_SAND, color: DARK_BG }}
                            whileTap={{ scale: 0.95 }}
                        >
                            Admin Chat
                        </motion.button>
                    </div>

                    {/* Mobile Menu Button */}
                    <div className="flex md:hidden">
                        <button
                            onClick={() => setIsMenuOpen(!isMenuOpen)}
                            className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
                        >
                            {isMenuOpen ? <X className="block h-6 w-6" style={{ color: ACCENT_SAND }} /> : <Menu className="block h-6 w-6" style={{ color: ACCENT_SAND }} />}
                        </button>
                    </div>
                </div>
            </div>

            {/* Mobile Menu */}
            <motion.div
                initial={{ height: 0 }}
                animate={{ height: isMenuOpen ? 'auto' : 0 }}
                transition={{ duration: 0.3 }}
                className="md:hidden overflow-hidden"
            >
                <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 flex flex-col items-center">
                    {navItems.map((item) => (
                        <a
                            key={item.name}
                            href={item.href}
                            onClick={() => setIsMenuOpen(false)}
                            className="block px-3 py-2 rounded-md text-base font-medium transition duration-200 hover:bg-gray-700 w-full text-center"
                            style={{ color: ACCENT_SAND }}
                        >
                            {item.name}
                        </a>
                    ))}
                    <motion.button
                        onClick={() => {
                            setIsMenuOpen(false);
                            currentView === 'public' ? toggleView('company-profile') : toggleView('public');
                        }}
                        className="mt-2 w-11/12 px-4 py-2 text-base font-semibold rounded-full transition duration-300 shadow-md"
                        style={{ backgroundColor: GLASS_ACCENT, color: DARK_BG }}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                    >
                        {currentView === 'public' ? 'Company Profile' : 'Main Page'}
                    </motion.button>
                    <motion.button
                        onClick={() => {
                            setIsMenuOpen(false);
                            toggleView('admin');
                        }}
                        className="mt-2 w-11/12 px-4 py-2 text-base font-semibold rounded-full transition duration-300 shadow-md border"
                        style={{ borderColor: GLASS_ACCENT, color: ACCENT_SAND }}
                        whileHover={{ scale: 1.05, backgroundColor: ACCENT_SAND, color: DARK_BG }}
                        whileTap={{ scale: 0.95 }}
                    >
                        Admin Chat
                    </motion.button>
                </div>
            </motion.div>
        </nav>
    );
};

// ===================================
// UPDATED HERO COMPONENT WITH PARALLAX
// ===================================

const Hero = () => {
    const targetRef = useRef(null);
    
    // Setup Framer Motion hooks for scroll tracking
    const { scrollYProgress } = useScroll({
        target: targetRef,
        // Define the target area for scroll tracking: from when the section starts to when it ends
        offset: ["start start", "end end"], 
    });

    // Parallax transformation for the far background (moves 50% slower than the foreground)
    const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "50%"]);
    
    // Parallax transformation for the dark overlay (moves 30% slower than the foreground)
    const overlayY = useTransform(scrollYProgress, [0, 1], ["0%", "30%"]); 

    return (
        <section 
            id="hero" 
            ref={targetRef} 
            className="relative h-[60vh] md:h-[80vh] flex items-center justify-center overflow-hidden" 
            style={{ backgroundColor: DARK_BG }}
        >
            {/* Background pattern/image layer - Parallax Effect Applied */}
            <motion.div 
                className="absolute inset-0 opacity-10 bg-gray-900 bg-cover bg-center"
                style={{ 
                    y: backgroundY, // Apply parallax movement
                    backgroundAttachment: 'fixed',
                    // Using a subtle pattern for effect clarity, but a solid color works too:
                    backgroundImage: `url('https://placehold.co/1200x800/${DARK_BG.substring(1)}/${ACCENT_SAND.substring(1)}?text=ARM+PATTERN')`,
                }}
            ></motion.div>
            
            {/* Dark Overlay Layer - Secondary Parallax Effect Applied */}
            <motion.div 
                className="absolute inset-0 opacity-80" 
                style={{ 
                    y: overlayY, // Apply secondary parallax movement
                    backgroundColor: DEEP_BROWN 
                }}
            ></motion.div>
            
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 text-center relative z-10">
                <h1 className="text-4xl md:text-6xl font-extrabold text-white mb-4 leading-tight">
                    <ScrambleText text="Powering Infrastructure with" className="inline-block" color="white" />
                    <span style={{ color: ACCENT_SAND }} className="block">
                        <ScrambleText text="Expert Manpower" className="inline-block" color={ACCENT_SAND} />
                    </span>
                </h1>
                <p 
                    className="text-lg md:text-xl text-gray-200 mb-8 max-w-3xl mx-auto transition duration-700 opacity-100"
                >
                    ARM Group delivers highly skilled technical, maintenance, and construction personnel across the Middle East.
                </p>
               <motion.a 
                href="#services" 
                // Framer Motion Animation Properties
                animate={{
                    // Keyframe-like animation for the box shadow (the "flame/pulse")
                    boxShadow: [
                        `0 0 10px ${GLASS_ACCENT}50`, // Subtle start
                        `0 0 25px ${GLASS_ACCENT}90`, // Intense glow
                        `0 0 10px ${GLASS_ACCENT}50`, // Fade back
                    ],
                }}
                transition={{
                    duration: 3, // Duration of one pulse cycle
                    ease: "easeInOut", // Smooth in and out
                    repeat: Infinity, // Make it infinite
                    repeatType: "reverse", // Smoothly reverse the animation
                }}

                className="mt-10 inline-block px-8 py-3 text-lg font-semibold rounded-full shadow-lg transition duration-300 transform hover:scale-110"
                style={{ 
                    backgroundColor: GLASS_ACCENT, 
                    color: DARK_BG,
                    fontWeight: 700,
                    // Initial subtle glow (Framer Motion overwrites the boxShadow property here)
                    boxShadow: `0 0 10px ${GLASS_ACCENT}50`, 
                }}
                >
                    Explore Our Services
                </motion.a>
            </div>
        </section>
    );
};

