const FramerFadeIn = ({ children, delay = 0, duration = 0.8, y = 30, once = true }) => {
    return (
        <motion.div
            initial={{ opacity: 0, y: y }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: duration, delay: delay, ease: "easeOut" }}
            viewport={{ once: once, amount: 0.1 }}
        >
            {children}
        </motion.div>
    );
};


