const Services = () => {
    
    // 1. Setup useRef for the container that generates the scroll distance
    const containerRef = useRef(null);
    
    // 2. Track the scroll progress of the container
    const { scrollYProgress } = useScroll({
        target: containerRef,
        // Start the effect when the container enters the viewport, end when it leaves
        offset: ["start end", "end start"]
    });
    
    // 3. Define the horizontal translation (x) based on scroll progress
    // x is transformed from 0% scroll progress (0px) to 100% scroll progress (-1300px)
    const x = useTransform(scrollYProgress, [0, 1], ["0px", "-1300px"]); 

    // Framer Motion Variants for vertical-in animation
    const itemVariants = {
        hidden: { scale: 0.9, opacity: 0, y: 30, rotate: -1 }, 
        show: { 
            scale: 1, 
            opacity: 1, 
            rotate: 0, 
            transition: { type: "spring", stiffness: 100, damping: 10 }
        }
    };

    return (
        <section id="services" className="py-16 md:py-24" style={{ backgroundColor: DARK_BG }}>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <div className="text-center mb-12">
                    <FramerFadeIn y={50}>
                        <h2 className="text-4xl md:text-5xl font-extrabold mb-4" style={{ color: GLASS_ACCENT }}>
                            <ScrambleText 
                                text="Our Specialized Manpower Solutions" 
                                className="inline-block" 
                                color={GLASS_ACCENT} 
                            />
                        </h2>
                        <p className="text-lg text-gray-400 max-w-3xl mx-auto">
                            We cover a full spectrum of workforce needs for capital-intensive industries.
                        </p>
                         <p className="text-sm text-gray-400 mt-2 font-bold" style={{ color: GLASS_ACCENT }}>
                            **MODERN SCROLL DEMO: Scroll down while viewing the next section to move the cards horizontally.**
                        </p>
                    </FramerFadeIn>
                </div>
            </div>

            {/* --- 1. SCROLL WRAPPER (Creates the vertical scroll space) --- */}
            {/* The height of 300vh creates extra vertical space. Scrolling through this space drives the horizontal movement. */}
            <div ref={containerRef} className="relative z-0" style={{ height: '300vh' }}>
                
                {/* --- 2. STICKY PINNED CONTAINER (Holds the viewport for horizontal movement) --- */}
                {/* This container stays in the viewport (h-screen) while the user scrolls vertically through the parent. */}
                <div className="sticky top-0 h-screen overflow-hidden flex items-center">
                    
                    <div className="w-full">
                        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                            
                            {/* --- 3. HORIZONTAL TRACK (The element that moves) --- */}
                            <motion.div 
                                // Apply the horizontal transform defined by the scroll progress
                                style={{ x }}
                                className="flex space-x-8 pb-8 md:pb-12"
                                initial="hidden"
                                whileInView="show"
                                viewport={{ once: true, amount: 0.1 }}
                            >
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={HardHat} title="Construction" description="Engineers, Foremen, Welders, and general labor for large-scale projects." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Wrench} title="Maintenance & Shutdown" description="Skilled technicians for industrial plant maintenance and critical shutdown operations." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Zap} title="Technical & HVAC" description="Experts in electrical systems, instrumentation, and Heating, Ventilation, and AC." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Shield} title="Support Staff" description="Trained security personnel, drivers, and administrative support roles." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Factory} title="Oil & Gas" description="Specialized manpower for oil rigs, refineries, and petrochemical plants." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Hotel} title="Hospitality" description="Soft services personnel, housekeeping, and facility management experts." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Building} title="Manufacturing" description="Assembly line workers, quality control inspectors, and logistics support." />
                                </motion.div>
                            </motion.div>

                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};


