import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Navbar from "./Navbar";
import Hero from "./Hero";
import About from "./About";
import Services from "./Services";
import Footer from "./Footer";
import AdminDashboard from "./AdminDashboard";
import CompanyProfilePage from "./CompanyProfilePage";

const App = () => {
  const [view, setView] = useState("public");
  const [isLoading, setIsLoading] = useState(true);

  // Toggle between public and admin views
  const toggleView = (target) => setView(target);

  // Simple loader (optional)
  useEffect(() => {
    const timer = setTimeout(() => setIsLoading(false), 800);
    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen bg-white">
        <div className="text-xl text-[#1F3B66] font-semibold animate-pulse">
          Loading...
        </div>
      </div>
    );
  }

  return (
    <div className="font-sans">
      <Routes>
        {/* üåê Main Public + Admin Dashboard Switch */}
        <Route
          path="/"
          element={
            view === "admin" ? (
              <AdminDashboard toggleView={toggleView} />
            ) : (
              <>
                <Navbar toggleView={toggleView} currentView={view} />
                <main>
                  <Hero />
                  <About />
                  <Services />
                  <Footer />
                </main>
              </>
            )
          }
        />

        {/* üè¢ Company Profile Page (Separate Page Route) */}
        <Route path="/company-profile" element={<CompanyProfilePage />} />

        {/* üîê Admin Dashboard (Direct Route Access) */}
        <Route
          path="/admin"
          element={<AdminDashboard toggleView={toggleView} />}
        />
      </Routes>
    </div>
  );
};

export default App;
