import React from 'react';
import useScrollAnimation from '../hooks/useScrollAnimation.js';

const Hero = () => {
    // Scroll animation hook: path corrected to "../hooks/useScrollAnimation.js"
    const [ref, isVisible] = useScrollAnimation({ threshold: 0.2 });

    // Placeholder image URL for the background
    const heroImage = "https://placehold.co/1920x800/215797/E8C046?text=ARM+Manpower+Solutions";

    return (
        <section 
            id="home" 
            className="relative h-screen flex items-center justify-center text-white overflow-hidden"
            ref={ref}
        >
            {/* Background Image/Overlay */}
            <div 
                className="absolute inset-0 bg-cover bg-center transition-opacity duration-1000 ease-in-out" 
                style={{ 
                    backgroundImage: `url(${heroImage})`,
                    opacity: isVisible ? 1 : 0
                }}
                aria-hidden="true"
            >
                {/* Dark Blue Overlay */}
                <div className="absolute inset-0 bg-arm-blue/70"></div>
            </div>

            {/* Content Container */}
            <div className="relative z-10 max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <h1 
                    className={`text-6xl sm:text-7xl lg:text-8xl font-extrabold mb-4 transition-all duration-1000 transform ${
                        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
                    }`}
                >
                    MANPOWER SOLUTIONS
                </h1>
                <p 
                    className={`text-xl sm:text-2xl font-light mb-8 transition-all duration-1000 delay-300 transform ${
                        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
                    }`}
                >
                    Powering Industries with Dedicated and Qualified Professionals
                </p>
                <a 
                    href="#services" 
                    className={`inline-block px-8 py-3 rounded-full text-lg font-semibold bg-arm-gold text-arm-blue hover:bg-arm-gold/90 transition duration-300 transform ${
                        isVisible ? 'opacity-100 scale-100' : 'opacity-0 scale-90'
                    }`}
                >
                    Explore Our Services
                </a>
            </div>
        </section>
    );
};

export default Hero;
