import React, { useState, useEffect, useRef } from 'react';
import { Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin, MapPin, Mail, Phone, Facebook, Twitter, Briefcase, ChevronRight, Fuel, UtensilsCrossed, Package, Factory, LayoutGrid, Handshake, Truck, Eye, Target, CheckCircle, Star, Heart, Lightbulb } from 'lucide-react';
import CompanyProfilePage from './components/CompanyProfilePage';
import About from './components/About';
import Services from './components/Services';
import Footer from './components/Footer';
import AdminDashboard from './components/AdminDashboard';

// ===================================
// 0. Color Definitions & Utilities
// ===================================
const SOFT_CREAM = '#FBF9F6';
const ARM_BLUE = '#1F3B66';
const ARM_GOLD = '#D4A74B';

const SCRAMBLE_CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=';
const DURATION = 1000;

const getRandomChar = () => SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];

const ScrambleText = ({ text, className, color = ARM_BLUE }) => {
    const [scrambledText, setScrambledText] = useState(text || '');
    const intervalRef = useRef(null);

    useEffect(() => {
        if (!text) return;
        let frame = 0;
        const totalFrames = 30;
        const intervalTime = DURATION / totalFrames;
        intervalRef.current = setInterval(() => {
            const newText = text.split('').map((char, index) => {
                const charProgress = (frame * intervalTime) / DURATION;
                const charDelay = index / text.length;
                if (charProgress > 0.5 + charDelay * 0.5) return char;
                return getRandomChar();
            }).join('');
            setScrambledText(newText);
            frame++;
            if (frame > totalFrames) {
                clearInterval(intervalRef.current);
                setScrambledText(text);
            }
        }, intervalTime);
        return () => clearInterval(intervalRef.current);
    }, [text]);

    return <span className={className} style={{ color }}>{scrambledText}</span>;
};

// ===================================
// 1. Navbar Component
// ===================================
const NavLink = ({ to, children, setIsOpen }) => (
    <a 
        href={`#${to}`} 
        onClick={() => setIsOpen && setIsOpen(false)}
        className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block"
    >
        {children}
    </a>
);

const Navbar = ({ toggleView, currentView }) => {
    const [isOpen, setIsOpen] = useState(false);
    const isPublicView = currentView === 'public';

    const openCompanyProfile = () => {
        window.open('/company-profile', '_blank', 'noopener,noreferrer');
    };

    return (
        <header className="sticky top-0 z-50 bg-white shadow-md">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                <div className="text-2xl font-bold text-arm-blue cursor-pointer" onClick={() => toggleView('public')}>
                    <ScrambleText text="ARM" className="inline-block" color={ARM_BLUE} /> 
                    <span className="text-arm-gold"> Group</span>
                </div>

                {/* Desktop Navigation */}
                <nav className="hidden lg:flex items-center space-x-8">
                    {isPublicView && (
                        <>
                            <NavLink to="hero">Home</NavLink>
                            <button 
                                onClick={openCompanyProfile}
                                className="text-gray-700 hover:text-arm-gold transition duration-200"
                            >
                                Company Profile
                            </button>
                            <NavLink to="about">About Us</NavLink>
                            <NavLink to="services">Services</NavLink>
                            <NavLink to="directors">Leadership</NavLink>
                            <NavLink to="footer">Contact</NavLink>
                        </>
                    )}
                    <button 
                        onClick={() => toggleView(isPublicView ? 'admin' : 'public')}
                        className={`px-4 py-2 text-sm font-semibold rounded-lg shadow-md transition duration-300 
                            ${isPublicView 
                                ? 'bg-arm-blue text-white hover:bg-arm-gold hover:text-arm-blue' 
                                : 'bg-arm-gold text-arm-blue hover:bg-arm-blue hover:text-white'}`}
                    >
                        {isPublicView ? 'Admin Login' : 'Back to Site'}
                    </button>
                </nav>

                {/* Mobile Menu Button */}
                <div className="lg:hidden">
                    <button onClick={() => setIsOpen(!isOpen)} className="p-2 rounded-md text-arm-blue hover:bg-gray-100">
                        {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                    </button>
                </div>
            </div>

            {/* Mobile Menu Dropdown */}
            {isOpen && (
                <div className="lg:hidden fixed inset-0 backdrop-blur-sm px-4 pt-2 pb-4 space-y-2 bg-white border-t">
                    {isPublicView && (
                        <>
                            <NavLink to="hero" setIsOpen={setIsOpen}>Home</NavLink>
                            <button
                                onClick={() => { openCompanyProfile(); setIsOpen(false); }}
                                className="w-full text-left px-4 py-2 text-gray-700 hover:text-[#D4A74B]"
                            >
                                Company Profile
                            </button>
                            <NavLink to="about" setIsOpen={setIsOpen}>About Us</NavLink>
                            <NavLink to="services" setIsOpen={setIsOpen}>Services</NavLink>
                            <NavLink to="directors" setIsOpen={setIsOpen}>Leadership</NavLink>
                            <NavLink to="footer" setIsOpen={setIsOpen}>Contact</NavLink>
                        </>
                    )}
                    <button 
                        onClick={() => { toggleView(isPublicView ? 'admin' : 'public'); setIsOpen(false); }}
                        className="w-full text-left px-4 py-2 bg-arm-blue text-white text-sm font-semibold rounded-md hover:bg-arm-gold hover:text-arm-blue mt-2"
                    >
                        {isPublicView ? 'Admin Login' : 'Back to Site'}
                    </button>
                </div>
            )}
        </header>
    );
};

// ===================================
// 2. Hero Component
// ===================================
const Hero = () => (
    <section id="hero" className="relative bg-white pt-16 pb-24 lg:pt-32 lg:pb-40 overflow-hidden">
