import React from 'react';
import { Users, Briefcase, TrendingUp } from 'lucide-react';
// FIX: Using standard relative path
import useScrollAnimation from '../hooks/useScrollAnimation.js';

const About = () => {
    const [ref, isVisible] = useScrollAnimation(0.1);

    const features = [
        { icon: Users, title: "Skilled Manpower", description: "Providing qualified professionals across multiple industrial sectors on a rental basis." },
        { icon: Briefcase, title: "Local & Overseas Recruitment", description: "Specializing in local transfers and large-scale overseas recruitment to meet diverse client needs." },
        { icon: TrendingUp, title: "Efficiency & Success", description: "Our services help clients maintain efficiency, reduce operational costs, and achieve long-term success." },
    ];

    return (
        <section id="about" ref={ref} className="section-container bg-white">
            <div className={`transition duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>
                <h2 className="heading-primary text-center mb-12">
                    About ARM Group
                </h2>

                <div className="lg:flex lg:space-x-12">
                    {/* Main Text */}
                    <div className="lg:w-1/2 mb-8 lg:mb-0">
                        <p className="text-lg text-gray-600 mb-6 leading-relaxed">
                            ARM Group specializes in providing skilled manpower on a rental and local transfer basis, as well as recruiting from overseas to meet diverse client requirements across multiple industries.
                        </p>
                        <p className="text-lg text-gray-600 leading-relaxed border-l-4 border-arm-gold pl-4 italic">
                            Our services ensure that clients have access to qualified professionals whenever needed, helping them maintain efficiency, reduce operational costs, and achieve long-term success.
                        </p>
                    </div>

                    {/* Feature Cards */}
                    <div className="lg:w-1/2 grid grid-cols-1 gap-8 md:grid-cols-3">
                        {features.map((feature, index) => {
                            const Icon = feature.icon;
                            return (
                                <div 
                                    key={index} 
                                    className="p-6 bg-arm-blue/5 rounded-xl shadow-lg hover:shadow-xl transition duration-300 transform hover:-translate-y-1"
                                >
                                    <Icon className="h-10 w-10 text-arm-gold mb-4" />
                                    <h3 className="text-xl font-semibold text-arm-blue mb-2">{feature.title}</h3>
                                    <p className="text-sm text-gray-600">{feature.description}</p>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default About;
