import React, { useState } from 'react';
import { Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin, MapPin, Mail, Phone, Facebook, Twitter } from 'lucide-react';

// Color definitions for Tailwind utility classes
const ARM_BLUE = '#1F3B66';
const ARM_GOLD = '#D4A74B';

// ===================================
// 1. Navbar Component
// ===================================

const NavLink = ({ to, children, setIsOpen }) => (
    <a 
        href={`#${to}`} 
        onClick={() => setIsOpen && setIsOpen(false)} // Close menu on mobile click
        className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block"
    >
        {children}
    </a>
);

const Navbar = ({ toggleView, currentView }) => {
    const [isOpen, setIsOpen] = useState(false);

    const isPublicView = currentView === 'public';

    return (
        <header className="sticky top-0 z-50 bg-white shadow-md">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                {/* Logo */}
                <div className="text-2xl font-bold" style={{ color: ARM_BLUE }}>
                    ARM <span style={{ color: ARM_GOLD }}>Group</span>
                </div>

                {/* Desktop Navigation */}
                <nav className="hidden lg:flex items-center space-x-8">
                    {isPublicView && (
                        <>
                            <NavLink to="hero">Home</NavLink>
                            <NavLink to="about">About Us</NavLink>
                            <NavLink to="services">Services</NavLink>
                            <NavLink to="directors">Leadership</NavLink>
                            <NavLink to="footer">Contact</NavLink>
                            {/* ADMIN LOGIN BUTTON RE-ADDED */}
                            <button 
                                onClick={() => toggleView('admin')}
                                className="px-4 py-2 text-white text-sm font-semibold rounded-full shadow-md transition duration-300 transform hover:scale-105"
                                style={{ backgroundColor: ARM_GOLD, hover: { backgroundColor: ARM_BLUE } }}
                            >
                                Admin Login
                            </button>
                        </>
                    )}
                    
                    {/* The Navbar will only show the "Back to Site" button if it is in the 
                    admin view, providing a way back.
                    */}
                    
                    {!isPublicView && (
                         <button 
                            onClick={() => toggleView('public')}
                            className="px-4 py-2 text-white text-sm font-semibold rounded-full shadow-md transition duration-300 transform hover:scale-105"
                            style={{ backgroundColor: ARM_BLUE, hover: { backgroundColor: ARM_GOLD, color: ARM_BLUE } }}
                        >
                            Back to Site
                        </button>
                    )}
                </nav>

                {/* Mobile Menu Button */}
                <button 
                    onClick={() => setIsOpen(!isOpen)}
                    className="lg:hidden text-gray-700 p-2 rounded-md hover:bg-gray-100 transition duration-200"
                >
                    {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
            </div>

            {/* Mobile Menu Dropdown */}
            {isOpen && (
                 <div className="lg:hidden px-4 pt-2 pb-4 space-y-2 bg-white border-t">
                    {isPublicView && (
                        <>
                            <NavLink to="hero" setIsOpen={setIsOpen}>Home</NavLink>
                            <NavLink to="about" setIsOpen={setIsOpen}>About Us</NavLink>
                            <NavLink to="services" setIsOpen={setIsOpen}>Services</NavLink>
                            <NavLink to="directors" setIsOpen={setIsOpen}>Leadership</NavLink>
                            <NavLink to="footer" setIsOpen={setIsOpen}>Contact</NavLink>
                            {/* ADMIN LOGIN BUTTON RE-ADDED FOR MOBILE */}
                            <button 
                                onClick={() => { toggleView('admin'); setIsOpen(false); }}
                                className="w-full text-left px-4 py-2 text-white text-sm font-semibold rounded-md shadow-md transition duration-300 mt-2"
                                style={{ backgroundColor: ARM_GOLD, hover: { backgroundColor: ARM_BLUE } }}
                            >
                                Admin Login
                            </button>
                        </>
                    )}
                    
                    {!isPublicView && (
                        <button 
                            onClick={() => { toggleView('public'); setIsOpen(false); }}
                            className="w-full text-left px-4 py-2 text-white text-sm font-semibold rounded-md shadow-md transition duration-300 mt-2"
                            style={{ backgroundColor: ARM_BLUE, hover: { backgroundColor: ARM_GOLD, color: ARM_BLUE } }}
                        >
                            Back to Site
                        </button>
                    )}
