        setStatus('success');
        setTimeout(() => setStatus(null), 4000);
        e.target.reset();
    };

    return (
        <FadeInSection>
            <section id="contact" className="py-16 md:py-24" style={{ backgroundColor: 'white' }}>
                <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="text-center mb-12">
