    // 2. Track the scroll progress of the container
    const { scrollYProgress } = useScroll({
        target: containerRef,
        // Start the effect when the container enters the viewport, end when it leaves
        offset: ["start end", "end start"]
    });
    
    // 3. Define the horizontal translation (x) based on scroll progress
    // -1300px is an estimated distance to scroll 7 cards off the left edge.
    const x = useTransform(scrollYProgress, [0, 1], ["0px", "-1300px"]); 

    // Framer Motion Variants for vertical-in animation
    const itemVariants = {
        hidden: { scale: 0.9, opacity: 0, y: 30, rotate: -1 }, 
        show: { 
            scale: 1, 
            opacity: 1, 
            rotate: 0, 
