import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion'; // <-- REQUIRED IMPORTS FOR THE EFFECT
import { 
    Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin, MapPin, Mail, Phone, 
    Facebook, Twitter, Factory, Hotel, Truck, Building, HeartPulse, ShoppingCart, Mic 
} from 'lucide-react'; 

// ===================================
// 0. Glassy Color Definitions & Utilities
// ===================================

// CORE THEME COLORS (Dark Blue Glassmorphism)
const DARK_BG = '#08101F';      // Deep dark blue/black for background
const GLASS_ACCENT = '#4B89F7'; // Soft Electric Blue (Accent/Primary)
const BRIGHT_TEXT = '#FAFAFA';  // White/light gray for text

// GLASS EFFECT STYLES (Used for card and navbar backgrounds)
const GLASS_BASE_BG = 'rgba(255, 255, 255, 0.08)';
const GLASS_BORDER = `1px solid ${GLASS_ACCENT}30`;
const GLASS_SHADOW = `0 8px 32px 0 rgba(0, 0, 0, 0.37)`;

// Apply to old color variables for consistency (using Dark Theme now)
const DEEP_BROWN = DARK_BG;
const ACCENT_SAND = GLASS_ACCENT;
const SOFT_CREAM = DARK_BG; 

// Hash Constants for Routing
const ADMIN_HASH = '#admin-key-123';
const COMPANY_PROFILE_HASH = '#company-profile-key-123';

// Characters used for the scrambling effect
const SCRAMBLE_CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=';
const DURATION = 1000; // Total duration of the scramble effect in ms

/**
 * ScrambleText Component (Animation Utility)
 */
const ScrambleText = ({ text, className, color = DEEP_BROWN }) => {
    const [scrambledText, setScrambledText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    const scramble = useCallback(() => {
        if (!text) return;

        const interval = Math.max(10, DURATION / text.length / 2); 
        let iteration = 0;

        const scrambler = setInterval(() => {
            const newText = text.split('').map((char, index) => {
                if (index < iteration) {
                    return text[index];
                }
                return SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];
            }).join('');

            setScrambledText(newText);
            
            if (iteration >= text.length) {
                clearInterval(scrambler);
                setIsComplete(true);
            }
            iteration += 1 / 2;
        }, interval);

        return () => clearInterval(scrambler);
    }, [text]);

    useEffect(() => {
        setIsComplete(false);
        const timeoutId = setTimeout(() => {
            scramble();
        }, 100); 

        return () => clearTimeout(timeoutId);
    }, [text, scramble]);

    return (
        <span 
            className={`${className} transition-all duration-300`} 
            style={{ 
                color: isComplete ? color : GLASS_ACCENT, 
                fontFamily: isComplete ? 'Inter, sans-serif' : 'monospace', 
            }}
        >
            {isComplete ? text : scrambledText}
        </span>
    );
};

// ===================================
// FRAMER MOTION SCROLLING EFFECT (Premium Fade-In)
// ===================================
/**
 * FramerFadeIn Component (Enhanced Scroll-based fade-in)
 * Creates the smooth, premium scroll effect.
 */
const FramerFadeIn = ({ children, delay = 0, duration = 0.8, y = 30, once = true }) => {
    return (
        <motion.div
            initial={{ opacity: 0, y: y }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: duration, delay: delay, ease: "easeOut" }}
            viewport={{ once: once, amount: 0.1 }}
        >
            {children}
        </motion.div>
    );
};

// ===================================
// 1. Navbar Component (Glassy Look & Navigation Fix)
// ===================================

const NavLink = ({ to, children, setIsOpen }) => {
    const handleNavigation = (e) => {
        e.preventDefault();
        
        if (setIsOpen) {
            setIsOpen(false);
        }

        setTimeout(() => {
            const targetElement = document.getElementById(to);
            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
            window.history.pushState(null, null, `#${to}`);
        }, 200); 
    };

    return (
        <a 
            href={`#${to}`} 
            onClick={handleNavigation}
            className="transition duration-300 block py-2 lg:py-0 lg:inline-block font-medium hover:scale-105"
            style={{ 
                color: BRIGHT_TEXT, 
                transition: 'color 0.3s', 
            }}
            onMouseEnter={(e) => {
                e.target.style.color = GLASS_ACCENT;
            }}
            onMouseLeave={(e) => {
                e.target.style.color = BRIGHT_TEXT;
            }}
        >
            {children}
        </a>
    );
};

const Navbar = ({ toggleView, currentView }) => {
    const [isOpen, setIsOpen] = useState(false);
    const isPublicView = currentView === 'public';

    // ðŸŒŸ FIX APPLIED: Uses the correct state key 'company-profile'
    const handleCompanyProfileClick = (e) => {
        e.preventDefault();
        setIsOpen(false);
        toggleView('company-profile'); 
    };

    return (
        <header 
            className={`sticky top-0 z-50 transition-all duration-300 backdrop-blur-md`}
            style={{ 
                // ðŸ’¡ GLASS EFFECT: High transparency and background blur
                backgroundColor: 'rgba(8, 16, 31, 0.8)', // Semi-transparent DARK_BG
                borderBottom: GLASS_BORDER,
                boxShadow: GLASS_SHADOW,
                backdropFilter: 'blur(10px)', // The core of the glass effect
            }}
        >
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                {/* Logo */}
                <div className="text-2xl font-bold">
                    <ScrambleText 
                        text="ARM Group" 
                        className="tracking-widest" 
                        color={BRIGHT_TEXT} 
                    />
                </div>

                {/* Desktop Navigation */}
                <nav className="hidden lg:flex items-center space-x-8">
                    {isPublicView && (
                        <>
                            <NavLink to="hero">Home</NavLink>
                            <NavLink to="services">Services</NavLink>
                            <NavLink to="directors">Leadership</NavLink>
                            <NavLink to="contact">Contact</NavLink> 
                            
                            {/* Company Profile Link */}
                            <a 
                                href="#company-profile"
                                onClick={handleCompanyProfileClick}
                                className="transition duration-300 block py-2 lg:py-0 lg:inline-block font-medium hover:scale-105"
                                style={{ color: BRIGHT_TEXT }}
                                onMouseEnter={(e) => { e.target.style.color = GLASS_ACCENT; }}
                                onMouseLeave={(e) => { e.target.style.color = BRIGHT_TEXT; }}
                            >
                                Company Profile
                            </a>
                        </>
                    )}
                    
                    {/* Admin Login Button */}
                    <button 
                        onClick={() => toggleView && toggleView(isPublicView ? 'admin' : 'public')}
                        className="px-4 py-2 text-sm font-semibold rounded-full shadow-md transition duration-300 transform hover:scale-105"
                        style={{ 
                            backgroundColor: isPublicView ? GLASS_ACCENT : DARK_BG, 
                            color: isPublicView ? DARK_BG : BRIGHT_TEXT,
                            border: `2px solid ${GLASS_ACCENT}`,
                        }}
                    >
                        {isPublicView ? 'Admin Login' : 'Back to Site'}
                    </button>
                </nav>

                {/* Mobile Menu Button */}
                <button 
                    onClick={() => setIsOpen(!isOpen)}
                    className="lg:hidden p-2 rounded-md transition duration-200 hover:bg-gray-800"
                    style={{ color: GLASS_ACCENT }}
                >
                    {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
            </div>

            {/* Mobile Menu Dropdown */}
            {isOpen && (
                <div 
                    className="lg:hidden px-4 pt-2 pb-4 space-y-2 border-t" 
                    style={{ backgroundColor: DARK_BG, borderColor: GLASS_ACCENT }}
                >
                    {isPublicView && (
                        <>
                            <NavLink to="hero" setIsOpen={setIsOpen}>Home</NavLink>
                            <NavLink to="services" setIsOpen={setIsOpen}>Services</NavLink>
                            <NavLink to="directors" setIsOpen={setIsOpen}>Leadership</NavLink>
                            <NavLink to="contact" setIsOpen={setIsOpen}>Contact</NavLink>
                             <a 
                                href="#company-profile"
                                onClick={handleCompanyProfileClick}
                                className="transition duration-300 block py-2 lg:py-0 lg:inline-block font-medium hover:scale-105"
                                style={{ color: BRIGHT_TEXT }}
                                onMouseEnter={(e) => { e.target.style.color = GLASS_ACCENT; }}
                                onMouseLeave={(e) => { e.target.style.color = BRIGHT_TEXT; }}
                            >
                                Company Profile
                            </a>
                        </>
                    )}
                    
                    <button 
                        onClick={() => { toggleView && toggleView(isPublicView ? 'admin' : 'public'); setIsOpen(false); }}
                        className="w-full text-left px-4 py-2 text-sm font-semibold rounded-md shadow-md transition duration-300 mt-2"
                         style={{ 
                            backgroundColor: isPublicView ? GLASS_ACCENT : DARK_BG, 
                            color: isPublicView ? DARK_BG : BRIGHT_TEXT,
                            border: `2px solid ${GLASS_ACCENT}`,
                        }}
                    >
                        {isPublicView ? 'Admin Login' : 'Back to Site'}
                    </button>
                </div>
            )}
        </header>
    );
};


// ===================================
// 2. Hero Component (Pulsing Button)
// ===================================

const Hero = () => (
    <section id="hero" className="relative h-[60vh] md:h-[80vh] flex items-center justify-center overflow-hidden" style={{ backgroundColor: DARK_BG }}>
        {/* Background pattern/image layer - Placeholder */}
        <div 
            className="absolute inset-0 opacity-10 bg-gray-900 bg-cover bg-center"
            style={{ 
                backgroundImage: "url('https://placehold.co/1200x800/08101F/FAFAFA?text=ARM+Pattern')",
                backgroundAttachment: 'fixed' 
            }}
        ></div>

        {/* Hero Content */}
        <div className="relative z-10 text-center px-4">
            <h1 className="text-5xl md:text-7xl font-extrabold mb-4 leading-tight">
                <ScrambleText text="ARM SOLUTIONS" className="text-white" color={BRIGHT_TEXT} />
            </h1>
            <p className="text-xl md:text-3xl font-light max-w-4xl mx-auto" style={{ color: BRIGHT_TEXT }}>
                <ScrambleText text="Manpower & Project Management for Industrial Excellence" className="text-white" color={BRIGHT_TEXT} />
            </p>
            
            {/* âš¡ EXPLORE SERVICES BUTTON WITH INFINITE PULSE GLOW */}
            <motion.a 
                href="#services" 
                animate={{
                    boxShadow: [
                        `0 0 10px ${GLASS_ACCENT}50`, 
                        `0 0 25px ${GLASS_ACCENT}90`, 
                        `0 0 10px ${GLASS_ACCENT}50`, 
                    ],
                }}
                transition={{
                    duration: 3, 
                    ease: "easeInOut", 
                    repeat: Infinity, 
                    repeatType: "reverse", 
                }}

                className="mt-10 inline-block px-8 py-3 text-lg font-semibold rounded-full shadow-lg transition duration-300 transform hover:scale-110"
                style={{ 
                    backgroundColor: GLASS_ACCENT, 
                    color: DARK_BG,
                    fontWeight: 700,
                    boxShadow: `0 0 10px ${GLASS_ACCENT}50`, 
                }}
            >
                Explore Services
            </motion.a>
        </div>
    </section>
);


// ===================================
// 3. About Component
// ===================================
const FeatureCard = ({ icon: Icon, title, description }) => (
    <FramerFadeIn y={30} delay={0.2} duration={0.8}>
        <div 
            className="p-8 rounded-xl shadow-lg border-t-4 backdrop-blur-sm h-full" 
            style={{ 
                backgroundColor: GLASS_BASE_BG, 
                border: GLASS_BORDER, 
                boxShadow: GLASS_SHADOW, 
                borderColor: GLASS_ACCENT, // Accent border
            }}
        >
            <Icon className="w-10 h-10 mb-4 mx-auto" style={{ color: GLASS_ACCENT }} />
            <h3 className="text-xl font-bold mb-2 text-center" style={{ color: BRIGHT_TEXT }}>{title}</h3>
            <p className="text-gray-400 text-center">{description}</p>
        </div>
    </FramerFadeIn>
);

const About = () => {
    return (
        <section id="about" className="py-16 md:py-24" style={{ backgroundColor: DARK_BG }}>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 max-w-7xl">
                <div className="text-center mb-12">
                     <FramerFadeIn y={50} duration={1.0}>
                        <h2 className="text-4xl md:text-5xl font-extrabold mb-4" style={{ color: GLASS_ACCENT }}>
                            <ScrambleText 
                                text="A Legacy of Trust and Excellence" 
                                className="inline-block" 
                                color={GLASS_ACCENT} 
                            />
                        </h2>
                        <p className="text-lg text-gray-400 max-w-3xl mx-auto">
                            For over two decades, ARM Group has been the reliable partner for major industrial and construction projects.
                        </p>
                    </FramerFadeIn>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                    <FeatureCard
                        icon={Globe}
                        title="Global Reach"
                        description="Sourcing and mobilizing top talent from key regions worldwide to meet localized project demands."
                    />
                    <FeatureCard
                        icon={Users}
                        title="Skilled Manpower"
                        description="Providing certified engineers, technicians, and specialized labor vetted for immediate deployment."
                    />
                    <FeatureCard
                        icon={TrendingUp}
                        title="Operational Efficiency"
                        description="Streamlining recruitment, logistics, and compliance processes to minimize project lead times."
                    />
                </div>
                
                 <FramerFadeIn y={50} delay={0.4} duration={0.8}>
                    <div className="text-center mt-12">
                        <button 
                            onClick={() => { window.location.hash = COMPANY_PROFILE_HASH; }}
                            className="px-6 py-3 text-lg font-semibold rounded-full shadow-md transition duration-300 transform hover:scale-105"
                            style={{ 
                                backgroundColor: DARK_BG, 
                                color: BRIGHT_TEXT,
                                border: `2px solid ${GLASS_ACCENT}`,
                                boxShadow: `0 0 10px ${GLASS_ACCENT}50`
                            }}
                        >
                            View Full Company Profile &rarr;
                        </button>
                    </div>
                </FramerFadeIn>
            </div>
        </section>
    );
};


// ===================================
// 4. Services Component (Scroll-Driven Horizontal Effect)
// ===================================

const ServiceCard = ({ icon: Icon, title, description, widthClass = "w-full" }) => (
    <motion.div 
        whileHover={{ 
            scale: 1.05, 
            boxShadow: `0 15px 40px rgba(75, 137, 247, 0.5)`,
            transition: { type: "spring", stiffness: 250, damping: 15 } 
        }}
        // widthClass is critical for the horizontal layout
        className={`group relative p-8 rounded-xl overflow-hidden cursor-pointer h-full backdrop-blur-sm flex-shrink-0 ${widthClass}`}
        style={{ 
            // ðŸ’¡ GLASS CARD STYLES
            backgroundColor: GLASS_BASE_BG, 
            border: GLASS_BORDER,
            boxShadow: GLASS_SHADOW,
            backdropFilter: 'blur(3px)', 
        }}
    >
        <div className="relative z-10">
            <div 
                className="flex items-center justify-center w-16 h-16 rounded-lg mb-6 transition duration-300 group-hover:scale-110 group-hover:rounded-2xl" 
                style={{ 
                    backgroundColor: GLASS_ACCENT, 
                    boxShadow: `0 0 15px ${GLASS_ACCENT}80`, 
                }}
            >
                <Icon className="w-8 h-8" style={{ color: DARK_BG }} /> 
            </div>
            
            <h3 
                className="text-2xl font-bold mb-3" 
                style={{ 
                    color: BRIGHT_TEXT, 
                }}
            >{title}</h3>
            <p className="text-gray-300">{description}</p>
        </div>
    </motion.div>
);

const Services = () => {
    
    // 1. Setup useRef for the container that generates the scroll distance
    const containerRef = useRef(null);
    
    // 2. Track the scroll progress of the container
    const { scrollYProgress } = useScroll({
        target: containerRef,
        // Start the effect when the container enters the viewport, end when it leaves
        offset: ["start end", "end start"]
    });
    
    // 3. Define the horizontal translation (x) based on scroll progress
    // -1300px is an estimated distance to scroll 7 cards off the left edge.
    const x = useTransform(scrollYProgress, [0, 1], ["0px", "-1300px"]); 

    // Framer Motion Variants for vertical-in animation
    const itemVariants = {
        hidden: { scale: 0.9, opacity: 0, y: 30, rotate: -1 }, 
        show: { 
            scale: 1, 
            opacity: 1, 
            rotate: 0, 
            transition: { type: "spring", stiffness: 100, damping: 10 }
        }
    };

    return (
        <section id="services" className="py-16 md:py-24" style={{ backgroundColor: DARK_BG }}>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <div className="text-center mb-12">
                    <FramerFadeIn y={50}>
                        <h2 className="text-4xl md:text-5xl font-extrabold mb-4" style={{ color: GLASS_ACCENT }}>
                            <ScrambleText 
                                text="Our Specialized Manpower Solutions" 
                                className="inline-block" 
                                color={GLASS_ACCENT} 
                            />
                        </h2>
                        <p className="text-lg text-gray-400 max-w-3xl mx-auto">
                            We cover a full spectrum of workforce needs for capital-intensive industries.
                        </p>
                         <p className="text-sm text-gray-400 mt-2 font-bold" style={{ color: GLASS_ACCENT }}>
                            **MODERN SCROLL DEMO: Scroll down while viewing the next section to move the cards horizontally.**
                        </p>
                    </FramerFadeIn>
                </div>
            </div>

            {/* --- 1. SCROLL WRAPPER (Creates the vertical scroll space) --- */}
            {/* The height of 300vh creates extra vertical space. Scrolling through this space drives the horizontal movement. */}
            <div ref={containerRef} className="relative z-0" style={{ height: '300vh' }}>
                
                {/* --- 2. STICKY PINNED CONTAINER (Holds the viewport for horizontal movement) --- */}
                {/* This container stays in the viewport (h-screen) while the user scrolls vertically through the parent. */}
                <div className="sticky top-0 h-screen overflow-hidden flex items-center">
                    
                    <div className="w-full">
                        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                            
                            {/* --- 3. HORIZONTAL TRACK (The element that moves) --- */}
                            <motion.div 
                                // Apply the horizontal transform defined above
                                style={{ x }}
                                className="flex space-x-8 pb-8 md:pb-12"
                                initial="hidden"
                                whileInView="show"
                                viewport={{ once: true, amount: 0.1 }}
                            >
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={HardHat} title="Construction" description="Engineers, Foremen, Welders, and general labor for large-scale projects." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Wrench} title="Maintenance & Shutdown" description="Skilled technicians for industrial plant maintenance and critical shutdown operations." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Zap} title="Technical & HVAC" description="Experts in electrical systems, instrumentation, and Heating, Ventilation, and AC." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Shield} title="Support Staff" description="Trained security personnel, drivers, and administrative support roles." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Factory} title="Oil & Gas" description="Specialized manpower for oil rigs, refineries, and petrochemical plants." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Hotel} title="Hospitality" description="Soft services personnel, housekeeping, and facility management experts." />
                                </motion.div>
                                <motion.div variants={itemVariants} className="flex-shrink-0 w-72 md:w-80">
                                    <ServiceCard icon={Building} title="Manufacturing" description="Assembly line workers, quality control inspectors, and logistics support." />
                                </motion.div>
                            </motion.div>

                        </div>
                    </div>
                </div>
            </div>
