// ===================================
// 10. Public View & App Logic
// ===================================

const PublicView = ({ toggleView, view }) => (
    <div className="flex flex-col min-h-screen">
        <main className="flex-grow">
            <Hero />
            <About />
            <Services />
            <Directors />
            <Contact />
        </main>
        <Footer />
    </div>
);

const PageTransitionWrapper = ({ children, isPublic }) => {
    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: isPublic ? 1 : 0 }} 
            exit={{ opacity: 0 }}
            transition={{ duration: 0.5 }}
            className="w-full"
        >
            {children}
        </motion.div>
    );
};


const App = () => {
    const getInitialView = () => {
        const path = window.location.hash;
        if (path.includes(ADMIN_HASH)) return 'admin';
        if (path.includes(COMPANY_PROFILE_HASH)) return 'company-profile';
        return 'public';
    };

    const [view, setView] = useState(getInitialView);

    const toggleView = (targetView) => {
        let targetHash = '';
        if (targetView === 'admin') targetHash = ADMIN_HASH;
        if (targetView === 'company-profile') targetHash = COMPANY_PROFILE_HASH;

        // Handle fade-out transition when leaving public view
        if (view === 'public' && (targetView === 'admin' || targetView === 'company-profile')) {
            setView('fading-out'); 
            setTimeout(() => {
                window.location.hash = targetHash;
                setView(targetView);
            }, 500); 
        } else {
            // Instant switch 
            window.location.hash = targetHash;
            setView(targetView);
        }
    };

    useEffect(() => {
        const handleHashChange = () => {
            const newView = getInitialView();
            if (view !== newView && view !== 'fading-out') {
                setView(newView);
            }
        };
        window.addEventListener('hashchange', handleHashChange);
        return () => window.removeEventListener('hashchange', handleHashChange);
    }, [view]);

    const currentView = view === 'fading-out' ? 'public' : view;

    let CurrentComponent;

    if (currentView === 'admin') {
        CurrentComponent = <AdminDashboard toggleView={toggleView} />;
    } else if (currentView === 'company-profile') {
        CurrentComponent = <CompanyProfile toggleView={toggleView} />;
    } else {
        CurrentComponent = (
            <PageTransitionWrapper isPublic={view === 'public'}>
                <PublicView toggleView={toggleView} view={currentView} />
            </PageTransitionWrapper>
        );
    }
    
    return (
        <div className="font-sans min-h-screen flex flex-col" style={{ backgroundColor: DARK_BG }}>
             {/* Navbar is rendered only when not in the full Admin view */}
             {currentView !== 'admin' && <Navbar toggleView={toggleView} currentView={currentView} />}

             <div className="flex-grow">
                 {CurrentComponent}
             </div>
        </div>
    );
};

export default App;
