// ====================================================================
// 7. Main App Component
// ====================================================================

const App = () => {
    // Inject custom brand colors via Tailwind configuration for 'arm-blue' and 'arm-gold'
    // NOTE: This is an inline script to define the colors for the CDN to pick up.
    useEffect(() => {
        const script = document.createElement('script');
        script.textContent = `
            tailwind.config = {
                theme: {
                    extend: {
                        colors: {
                            'arm-blue': '#1e3a8a', // Dark Blue (e.g., Royal Blue)
                            'arm-gold': '#f59e0b', // Amber/Gold (Accent)
                        },
                        fontFamily: {
                            sans: ['Inter', 'sans-serif'],
                        }
                    }
                }
            }
        `;
        document.head.appendChild(script);
        return () => {
            document.head.removeChild(script);
        };
    }, []);

    return (
        <div className="min-h-screen bg-gray-100 font-sans">
            {/* Tailwind CDN Load (Mandatory) */}
            <script src="https://cdn.tailwindcss.com"></script>
            
            {/* Add Inter Font (Mandatory) */}
            <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700;800;900&display=swap" rel="stylesheet" />
            
            <Header />
            <main>
                <Hero />
                <About />
                <Services />
                <Contact />
            </main>
            <Footer />
        </div>
    );
};

export default App;
