import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import {
  Menu, X, Globe, Users, TrendingUp, HardHat, Wrench, Zap, Shield, Linkedin,
  MapPin, Mail, Phone, Facebook, Twitter, Briefcase, ChevronRight, Fuel, UtensilsCrossed,
  Package, Factory, LayoutGrid, Handshake, Truck, Eye, Target, CheckCircle, Star, Heart, Lightbulb
} from "lucide-react";

import CompanyProfilePage from "./CompanyProfilePage"; // ✅ your separate component

// Define global colors
const SOFT_CREAM = "#FBF9F6";
const ARM_BLUE = "#1F3B66";
const ARM_GOLD = "#D4A74B";

// Utility for initial view detection
const getInitialView = () => {
  if (window.location.hash === "#admin-key-123") {
    return "admin";
  }
  return "public";
};

// --------------------------------------
// ScrambleText utility for logo animation
// --------------------------------------
const SCRAMBLE_CHARS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%^&*()_+-=";
const getRandomChar = () =>
  SCRAMBLE_CHARS[Math.floor(Math.random() * SCRAMBLE_CHARS.length)];

const ScrambleText = ({ text, className, color = ARM_BLUE }) => {
  const [scrambledText, setScrambledText] = useState(text);
  useEffect(() => {
    if (!text) return;
    let frame = 0;
    const totalFrames = 30;
    const interval = setInterval(() => {
      const newText = text
        .split("")
        .map((char, i) =>
          Math.random() < frame / totalFrames ? char : getRandomChar()
        )
        .join("");
      setScrambledText(newText);
      if (++frame > totalFrames) clearInterval(interval);
    }, 30);
    return () => clearInterval(interval);
  }, [text]);
  return <span className={className} style={{ color }}>{scrambledText}</span>;
};

// --------------------------------------
// Navbar
// --------------------------------------
import { Link } from "react-router-dom";

const NavLink = ({ to, children, setIsOpen }) => (
  <a
    href={`#${to}`}
    onClick={() => setIsOpen && setIsOpen(false)}
    className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2 lg:py-0 lg:inline-block"
  >
    {children}
  </a>
);

const Navbar = ({ toggleView, currentView }) => {
  const [isOpen, setIsOpen] = useState(false);
  const isPublicView = currentView === "public";

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
        {/* Logo */}
        <div className="text-2xl font-bold text-arm-blue">
          <ScrambleText text="ARM" color={ARM_BLUE} />{" "}
          <span className="text-arm-gold">Group</span>
        </div>

        {/* Desktop Navigation */}
        <nav className="hidden lg:flex items-center space-x-8">
          {isPublicView && (
            <>
              <NavLink to="hero">Home</NavLink>
              <Link
                to="/company-profile"
                className="text-gray-700 hover:text-arm-gold transition duration-200 block py-2"
              >
                Company Profile
              </Link>
              <NavLink to="about">About Us</NavLink>
              <NavLink to="services">Services</NavLink>
              <NavLink to="directors">Leadership</NavLink>
              <NavLink to="footer">Contact</NavLink>
            </>
          )}

          <button
            onClick={() => toggleView(isPublicView ? "admin" : "public")}
            className={`px-4 py-2 text-sm font-semibold rounded-lg shadow-md transition duration-300 
                ${
                  isPublicView
                    ? "bg-arm-blue text-white hover:bg-arm-gold hover:text-arm-blue"
                    : "bg-arm-gold text-arm-blue hover:bg-arm-blue hover:text-white"
                }`}
          >
            {isPublicView ? "Admin Login" : "Back to Site"}
          </button>
        </nav>

        {/* Mobile Menu Button */}
        <div className="lg:hidden">
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="p-2 rounded-md text-arm-blue hover:bg-gray-100"
          >
            {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
      </div>

      {/* Mobile Dropdown */}
      {isOpen && (
        <div className="lg:hidden fixed inset-0 backdrop-blur-sm px-4 pt-2 pb-4 space-y-2 bg-white border-t">
          {isPublicView && (
            <>
              <NavLink to="hero" setIsOpen={setIsOpen}>
                Home
              </NavLink>
              <Link
                to="/company-profile"
                onClick={() => setIsOpen(false)}
                className="block py-2 text-gray-700 hover:text-arm-gold"
              >
                Company Profile
              </Link>
              <NavLink to="about" setIsOpen={setIsOpen}>
                About Us
              </NavLink>
              <NavLink to="services" setIsOpen={setIsOpen}>
                Services
              </NavLink>
              <NavLink to="directors" setIsOpen={setIsOpen}>
                Leadership
              </NavLink>
              <NavLink to="footer" setIsOpen={setIsOpen}>
                Contact
              </NavLink>
            </>
          )}
          <button
            onClick={() => {
              toggleView(isPublicView ? "admin" : "public");
              setIsOpen(false);
            }}
            className="w-full px-4 py-2 bg-arm-blue text-white rounded-md shadow-md hover:bg-arm-gold hover:text-arm-blue transition duration-300"
          >
            {isPublicView ? "Admin Login" : "Back to Site"}
          </button>
        </div>
      )}
    </header>
  );
};

// --------------------------------------
// Placeholder Components for Layout
// --------------------------------------
const Hero = () => (
  <section id="hero" className="py-20 text-center bg-gray-50">
