// --------------------------------------
// Public + Admin Views
// --------------------------------------
const AdminDashboard = ({ toggleView }) => (
  <div className="min-h-screen bg-gray-100 p-8">
    <h2 className="text-4xl font-extrabold text-arm-blue mb-6">
      Admin Dashboard
    </h2>
    <button
      onClick={() => toggleView("public")}
      className="px-6 py-2 bg-arm-gold text-arm-blue font-semibold rounded-md hover:bg-arm-blue hover:text-white"
    >
      Back to Public Site
    </button>
  </div>
);

const PublicView = ({ toggleView, view }) => (
  <div className="bg-soft-cream min-h-screen">
    <Navbar toggleView={toggleView} currentView={view} />
    <main>
      <Hero />
      <About />
      <Services />
      <Directors />
    </main>
    <Footer />
  </div>
);

// --------------------------------------
// MAIN APP (Now Router-enabled)
// --------------------------------------
export default function App() {
  const [view, setView] = useState(getInitialView());
  const currentView = view === "fading-out" ? "public" : view;

  const toggleView = (targetView) => {
    if (view === targetView) return;
    if (view === "public") {
      setView("fading-out");
      setTimeout(() => {
        window.location.hash = "admin-key-123";
        setView("admin");
      }, 500);
    } else {
      if (targetView === "admin") window.location.hash = "admin-key-123";
      else window.location.hash = "";
      setView(targetView);
    }
  };

  useEffect(() => {
    const handleHashChange = () => {
      const newView = getInitialView();
      if (view !== newView && view !== "fading-out") {
        setView(newView);
      }
    };
    window.addEventListener("hashchange", handleHashChange);
    return () => window.removeEventListener("hashchange", handleHashChange);
  }, [view]);

  return (
    <Router>
      <Routes>
        {/* Public/Admin Layout */}
        <Route
          path="/"
          element={
            currentView === "admin" ? (
              <AdminDashboard toggleView={toggleView} />
            ) : (
              <PublicView toggleView={toggleView} view={currentView} />
            )
          }
        />

        {/* ✅ Separate Company Profile Page */}
        <Route path="/company-profile" element={<CompanyProfilePage />} />
      </Routes>
    </Router>
  );
}
