// ===================================
// 9. NEW SAMPLE PAGE COMPONENT
// ===================================

const SamplePage = ({ toggleView }) => {
    // Generate text for "lorem*25"
    const baseLorem = "Lorem ipsum dolor sit amet consectetur adipisicing elit. Laborum, quas. Voluptatum, quo. Dolores, reiciendis. Voluptatibus, quisquam. Expedita, sequi. Consequatur, minima. Quibusdam, sunt. Inventore, velit. Eaque, iste. Ullam, provident. Sunt, temporibus. Laborum, voluptate. Molestiae, magnam. Consequuntur, natus. Quam, veritatis. Necessitatibus, aliquid. Facilis, dolorum. Repudiandae, earum. Nihil, dicta. Distinctio, autem. Quia, et. Voluptates, quod. Ratione, id. Fugiat, esse. Corporis, ex. Quae, ab. Recusandae, minus. ";
    const loremText = baseLorem.repeat(25); 

    return (
        <div className="min-h-screen p-8" style={{ backgroundColor: SOFT_CREAM }}>
            <div className="container mx-auto max-w-4xl pt-24 pb-12">
                <h1 className="text-4xl md:text-5xl font-extrabold mb-8" style={{ color: DEEP_BROWN }}>
                    <ScrambleText text="Sample Dynamic Page" />
                </h1>
                <p className="text-gray-700 leading-relaxed text-lg whitespace-pre-wrap">
                    {loremText}
                </p>
                
                <div className="mt-12 text-center">
                    <button 
                        onClick={() => { toggleView('public'); window.location.hash = ''; }}
                        className="px-6 py-3 text-base font-semibold rounded-full shadow-md transition duration-300 transform hover:scale-105"
                        style={{ 
                            backgroundColor: ACCENT_SAND, 
                            color: DEEP_BROWN,
                            border: `2px solid ${DEEP_BROWN}`
                        }}
                    >
                        &larr; Back to Main Site
                    </button>
                </div>
            </div>
        </div>
    );
};


// ===================================
// 10. Public View Wrapper
// ===================================

const PublicView = ({ toggleView }) => (
    <>
        <main className="flex-grow">
            <Hero />
            <About />
            <Services />
            <Directors />
            <Contact />
        </main>
        <Footer />
    </>
);


// ===================================
// 11. Main App Component & Routing
// ===================================

const ADMIN_HASH = '#admin-key-123';
const SAMPLE_HASH = '#sample';

const getInitialView = () => {
    if (window.location.hash.includes(ADMIN_HASH)) return 'admin';
    if (window.location.hash.includes(SAMPLE_HASH)) return 'sample';
    return 'public';
};

export default function App() {
    // The view state can be 'public', 'admin', 'sample', or 'fading-out'
    const [view, setView] = useState(getInitialView());

    // Toggle view function: used by Navbar and AdminDashboard buttons
    const toggleView = (targetView) => {
        if (targetView !== 'public' && targetView !== 'admin' && targetView !== 'sample') {
            return; 
        }

        // Determine the hash for the target view
        let targetHash = '';
        if (targetView === 'admin') targetHash = ADMIN_HASH;
        if (targetView === 'sample') targetHash = SAMPLE_HASH;

        // Apply fade-out effect when leaving the main public view
        if (view === 'public' && (targetView === 'admin' || targetView === 'sample')) {
            setView('fading-out'); 
            setTimeout(() => {
                window.location.hash = targetHash;
                setView(targetView);
            }, 500); // Wait for PageTransitionWrapper to fade out
        } else {
            // Instant switch (e.g., admin to public, or public to section scroll)
            window.location.hash = targetHash;
            setView(targetView);
        }
    };

    // Listen for browser back/forward buttons or manual URL hash changes
    useEffect(() => {
        const handleHashChange = () => {
            const newView = getInitialView();
            // Ignore hash changes triggered by internal smooth-scrolling NavLinks
            // Only update view if the main route has changed.
            if (newView !== 'public' && view !== newView && view !== 'fading-out') {
                setView(newView);
            } else if (newView === 'public' && view !== 'public' && view !== 'fading-out') {
                 // Check if we are returning from a special route
                 setView(newView);
            }
        };
        window.addEventListener('hashchange', handleHashChange);
        return () => window.removeEventListener('hashchange', handleHashChange);
    }, [view]);

    // Determine the component to render
    const currentView = view === 'fading-out' ? 'public' : view;

    return (
        <div className="font-sans min-h-screen flex flex-col" style={{ backgroundColor: SOFT_CREAM }}>
            {/* Navbar is rendered for public and sample views */}
            {currentView !== 'admin' && <Navbar toggleView={toggleView} currentView={currentView} />}
            
            <div className="flex-grow">
                {currentView === 'admin' && (
                    // The Admin Dashboard remains static
                    <AdminDashboard toggleView={toggleView} />
                )}
                
                {currentView === 'sample' && (
                    // The new Sample Page is rendered full-page
                    <SamplePage toggleView={toggleView} />
                )}

                {(currentView === 'public' || view === 'fading-out') && (
                    // The Public View is wrapped in the transition logic
                    <PageTransitionWrapper isPublic={view === 'public'}>
                        <PublicView toggleView={toggleView} />
                    </PageTransitionWrapper>
                )}
            </div>
        </div>
    );
};
